import React, { Component } from "react";
import CanvasJSReact from "../canvasjs.react";
var CanvasJSChart = CanvasJSReact.CanvasJSChart;
var CanvasJS = CanvasJSReact.CanvasJS;
export class GraficoAutonomia extends Component {
  constructor() {
    super();
    this.toggleDataSeries = this.toggleDataSeries.bind(this);
  }
  toggleDataSeries(e) {
    if (typeof e.dataSeries.visible === "undefined" || e.dataSeries.visible) {
      e.dataSeries.visible = false;
    } else {
      e.dataSeries.visible = true;
    }

    this.chart.render();
  }

  render() {
    const { tarjetas } = this.props;

    // Formulas para "Indice de autonomia"

    // Filtro todos los meses en el que hay tarjetas convertidas
    const fechasTarjetasConvertidas = tarjetas.map(({ fecha }) =>
      fecha.substr(0, 7)
    );

    // Borro todos los meses repetidos
    let fechasTarjetasConvertidas1 = new Set(fechasTarjetasConvertidas);
    const fechasTarjetasConvertidasUnicas = [...fechasTarjetasConvertidas1];

    console.log(fechasTarjetasConvertidasUnicas);

    // Numero total de tarjetas de cada mes (no acumulado)
    let array = fechasTarjetasConvertidasUnicas.sort().map((item, index) => {
      return (
        tarjetas.filter(
          ({ estado, fecha, color }) =>
            color === "Azul" &&
            estado === "Cerrada" &&
            fecha.substr(4, 4).replace("-", "").replace("-", "") ===
              item.slice(5, 7)
        ).length +
        (tarjetas.filter(
          ({ estado, fecha, convertida }) =>
            convertida === true &&
            estado === "Cerrada" &&
            fecha.substr(4, 4).replace("-", "").replace("-", "") ===
              item.slice(5, 7)
        ).length /
          tarjetas.filter(
            ({ estado, fecha }) =>
              estado === "Cerrada" &&
              fecha.substr(4, 4).replace("-", "").replace("-", "") ===
                item.slice(5, 7)
          ).length) *
          100
      );
    });

    // Acumulado de tarjetas por mes
    const arrTarjetasAcumuladas = array.map((elem, index) =>
      array.slice(0, index + 1).reduce((aa, bb) => aa + bb)
    );

    // Datos para el grafico
    const ConvertidasData = [
      fechasTarjetasConvertidasUnicas.sort().map((item, index) => {
        return {
          x: new Date(
            parseInt(item.slice(0, 4)),
            parseInt(item.slice(5, 7) - 1)
          ),
          y: arrTarjetasAcumuladas[index],
        };
      }),
    ];

    CanvasJS.addCultureInfo("es", {
      decimalSeparator: ",", // Observe ToolTip Number Format
      digitGroupSeparator: ".", // Observe axisY labels

      months: [
        "Enero",
        "Febrero",
        "Marzo",
        "Abril",
        "Mayo",
        "Junio",
        "Julio",
        "Agosto",
        "Septiembre",
        "Octubre",
        "Noviembre",
        "Diciembre",
      ],
    });
    const options = {
      culture: "es",
      animationEnabled: true,
      culture: "es",
      axisX: {
        valueFormatString: "MMMM",

        interval: 1,
        intervalType: "month",
      },
      axisY: {
        suffix: "%",
        lineColor: "#000000",
        tickColor: "#000000",
        labelFontColor: "#000000",
      },

      toolTip: {
        shared: true,
      },
      legend: {
        cursor: "pointer",
        itemclick: this.toggleDataSeries,
        verticalAlign: "top",
      },
      data: [
        {
          color: "#1BA7F7",
          type: "column",
          name: "Indice de autonomia",
          showInLegend: true,
          xValueFormatString: "MMMM YYYY",
          dataPoints: ConvertidasData[0],
        },
      ],
    };

    return (
      <div>
        <CanvasJSChart
          culture="en"
          options={options}
          onRef={(ref) => (this.chart = ref)}
        />
      </div>
    );
  }
}

export default GraficoAutonomia;
