import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import {
  Card,
  CardBody,
  Row,
  Col,
  Table,
  Button,
  Container,
  Input,
  Label,
} from "reactstrap";
import moment from "moment";
import Select from "react-select";
const options = [
  { value: "NÂ°", label: "numero" },
  { value: "Color", label: "color" },
  { value: "Prioridad", label: "prioridad" },
  { value: "Equipo Autonomo", label: "equipo" },
  { value: "Fecha apertura", label: "fecha" },
  { value: "Descripcion anomalia", label: "descripcion" },
  { value: "Estado acutal", label: "estado" },
];
class MisTarjetasFiltro extends Component {
  componentDidMount() {
    this.props.getTarjetas();
  }
  state = {
    selectedOption: null,
    color: null,
    equipo: null,
    prioridad: null,
  };
  handleChange = (selectedOption) => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption);
  };
  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  render() {
    const { tarjetas } = this.props.tarjetas;
    const { selectedOption } = this.state;
    console.log(this.state);
    const filter = {
      color: "Azul",
      prioridad: "Alta",
    };
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <Row>
                <Col>
                  <div>
                    <h2 className="mb-3">Tabla dinamica de tarjetas</h2>
                  </div>
                </Col>
              </Row>
              <div className="mb-3">
                <h5>Seleccionar elementos para la tabla</h5>
                <Select
                  value={selectedOption}
                  onChange={this.handleChange}
                  options={options}
                  isMulti
                  placeholder="Seleccionar"
                />
              </div>
              <h5>Filtrar elementos</h5>
              <Row className="mb-3">
                {selectedOption &&
                  selectedOption.map(({ label, value }) => {
                    return (
                      <Col>
                        <div>
                          <Label for="color">{value}</Label>
                          <Input
                            type="select"
                            name={label}
                            id={label}
                            onChange={this.onChange}
                          >
                            <option>Seleccionar</option>
                            {tarjetas.map((item) => (
                              <option>{item[label]}</option>
                            ))}
                          </Input>
                        </div>
                      </Col>
                    );
                  })}
              </Row>
              <Card>
                <CardBody>
                  <Table className="no-wrap v-middle" responsive>
                    <thead>
                      <tr className="border-0">
                        {selectedOption &&
                          selectedOption.map(({ value }) => {
                            return <th className="border-0">{value}</th>;
                          })}
                      </tr>
                    </thead>

                    {tarjetas &&
                      tarjetas
                        .filter(({ color }) => color === "Azul")
                        .map((item, index) => {
                          return (
                            <tbody key={index}>
                              <tr>
                                {selectedOption &&
                                  selectedOption.map(
                                    ({ value, label }, index) => {
                                      return <td key={index}>{item[label]}</td>;
                                    }
                                  )}
                              </tr>
                            </tbody>
                          );
                        })}
                  </Table>
                </CardBody>
              </Card>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
    user: state.auth.user,
  };
};
export default connect(mapStateToProps, { getTarjetas })(MisTarjetasFiltro);
