import {
  GET_KAIZEN,
  CARGANDO_KAIZEN,
  AGREGAR_KAIZEN,
  AGREGAR_PLANIFICACION,
  AGREGAR_IMAGEN,
} from "../actions/types";
const initState = {
  tarjetaskaizen: [],
  cargando: false,
  agregarsuccess: false,
  tarjetaActualId: "",
};

export default function (state = initState, action) {
  switch (action.type) {
    case GET_KAIZEN:
      return {
        ...state,
        tarjetaskaizen: action.payload,
        cargando: false,
        agregarsuccess: false,
        tarjetaActualId: "",
      };
    case AGREGAR_KAIZEN:
      return {
        ...state,
        tarjetaskaizen: [action.payload, ...state.tarjetas],
        agregarsuccess: true,
        tarjetaActualId: action.payload._id,
      };
    case CERRAR_TARJETA:
    case EDITAR_TARJETA:
      return Object.assign({}, state, {
        tarjetaskaizen: state.tarjetas.map((tarjeta) => {
          return tarjeta._id === action.payload._id ? action.payload : tarjeta;
        }), // replace matched item and returns the array
        agregarsuccess: true,
      });
    case BORRAR_TARJETA:
      return {
        ...state,
        tarjetaskaizen: state.tarjetas.filter(
          (tarjeta) => tarjeta._id !== action.payload
        ),
      };
    case CARGANDO_KAIZEN:
      return {
        ...state,
        cargando: true,
      };
    case AGREGAR_PLANIFICACION:
      return {
        ...state,
        tarjetaskaizen: [action.payload],
      };
    case AGREGAR_IMAGEN:
      return {
        ...state,
        tarjetaskaizen: [action.payload],
      };
    default:
      return state;
  }
}
