import React, { Component } from "react";
import { connect } from "react-redux";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import {
  TabContent,
  TabPane,
  Nav,
  NavItem,
  NavLink,
  Card,
  Button,
  CardTitle,
  CardText,
  Row,
  Col,
  CardBody,
} from "reactstrap";
import classnames from "classnames";
import GraficoAmarillas from "./tarjetasamarillas/GraficoAmarillas";
import GraficoRojas from "./tarjetasrojas/GraficoRojas";
import GraficoAzules from "./tarjetasazules/GraficoAzules";
import GraficoVerdes from "./tarjetasverdes/GraficoVerdes";
import GraficoAutonomia from "./indiceautonomia/GraficoAutonomia";
import GraficoFiltro from "./tarjetasfiltro/GraficoFiltro";

class DashBoard extends Component {
  state = {
    activeTab: "1",
  };
  componentDidMount() {
    this.props.getTarjetas();
  }
  toggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({ activeTab: tab });
    }
  }
  render() {
    const { tarjetas } = this.props.tarjetas;
    const { isAuthenticated, isLoading } = this.props;
    // if (isAuthenticated === false && isLoading === false)
    //   return <Redirect to="/login" />;
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <div>
              <Nav tabs>
                <NavItem>
                  <NavLink
                    className={classnames({
                      active: this.state.activeTab === "1",
                    })}
                    onClick={() => {
                      this.toggle("1");
                    }}
                  >
                    Tarjetas Amarillas
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={classnames({
                      active: this.state.activeTab === "2",
                    })}
                    onClick={() => {
                      this.toggle("2");
                    }}
                  >
                    Tarjetas Rojas
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={classnames({
                      active: this.state.activeTab === "3",
                    })}
                    onClick={() => {
                      this.toggle("3");
                    }}
                  >
                    Tarjetas Verdes
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={classnames({
                      active: this.state.activeTab === "4",
                    })}
                    onClick={() => {
                      this.toggle("4");
                    }}
                  >
                    Tarjetas Azules
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={classnames({
                      active: this.state.activeTab === "5",
                    })}
                    onClick={() => {
                      this.toggle("5");
                    }}
                  >
                    Indice de autonomia
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={classnames({
                      active: this.state.activeTab === "6",
                    })}
                    onClick={() => {
                      this.toggle("6");
                    }}
                  >
                    Grafico personalizado
                  </NavLink>
                </NavItem>
              </Nav>
              <TabContent activeTab={this.state.activeTab}>
                <TabPane tabId="1">
                  <Row>
                    <Col sm="12">
                      <Card>
                        <CardBody>
                          <div>
                            <h3 className="mb-3">
                              Evolucion de Tarjetas amarillas
                            </h3>
                          </div>
                          <GraficoAmarillas
                            tarjetas={tarjetas}
                          ></GraficoAmarillas>
                        </CardBody>
                      </Card>
                    </Col>
                  </Row>
                </TabPane>
                <TabPane tabId="2">
                  <Row>
                    <Col sm="12">
                      <Card>
                        <CardBody>
                          <div>
                            <h3 className="mb-3">
                              Evolucion de Tarjetas rojas
                            </h3>
                          </div>
                          <GraficoRojas tarjetas={tarjetas}></GraficoRojas>
                        </CardBody>
                      </Card>
                    </Col>
                  </Row>
                </TabPane>
                <TabPane tabId="3">
                  <Row>
                    <Col sm="12">
                      <Card>
                        <CardBody>
                          <div>
                            <h3 className="mb-3">
                              Evolucion de Tarjetas verdes
                            </h3>
                          </div>
                          <GraficoVerdes tarjetas={tarjetas}></GraficoVerdes>
                        </CardBody>
                      </Card>
                    </Col>
                  </Row>
                </TabPane>
                <TabPane tabId="4">
                  <Row>
                    <Col sm="12">
                      <Card>
                        <CardBody>
                          <div>
                            <h3 className="mb-3">
                              Evolucion de Tarjetas azules
                            </h3>
                          </div>
                          <GraficoAzules tarjetas={tarjetas}></GraficoAzules>
                        </CardBody>
                      </Card>
                    </Col>
                  </Row>
                </TabPane>
                <TabPane tabId="5">
                  <Row>
                    <Col sm="12">
                      <Card>
                        <CardBody>
                          <div>
                            <h3 className="mb-3">Indice de autonomia</h3>
                          </div>
                          <GraficoAutonomia
                            tarjetas={tarjetas}
                          ></GraficoAutonomia>
                        </CardBody>
                      </Card>
                    </Col>
                  </Row>
                </TabPane>
                <TabPane tabId="6">
                  <Row>
                    <Col sm="12">
                      <GraficoFiltro tarjetas={tarjetas}></GraficoFiltro>
                    </Col>
                  </Row>
                </TabPane>
              </TabContent>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
    isAuthenticated: state.auth.isAuthenticated,
    isLoading: state.auth.isLoading,
    user: state.auth.user,
  };
};
export default connect(mapStateToProps, { getTarjetas })(DashBoard);
