import React, { Component } from "react";
import { connect } from "react-redux";
import { editarTarjeta } from "../../../store/actions/tarjetaActions";
import { clearErrors } from "../../../store/actions/errorActions";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  Label,
  Input,
  FormGroup,
  NavLink,
  Alert,
  Row,
  Col,
} from "reactstrap";

class EditarTarjetaModalAmarilla extends Component {
  state = {
    modal: false,
    numero: "",
    _id: this.props.tarjeta._id,
    descripcion: this.props.tarjeta.descripcion,
    detecto: this.props.tarjeta.detecto,
    prioridad: this.props.tarjeta.prioridad,
    familia: this.props.tarjeta.familia,
    maquina: this.props.tarjeta.maquina,
    equipo: this.props.tarjeta.equipo,
    riesgoInicial: this.props.tarjeta.riesgoInicial,
    tipodeRiesgo: this.props.tarjeta.tipodeRiesgo,
    sugerencia: this.props.tarjeta.sugerencia,
    sustoExperimentado: this.props.tarjeta.sustoExperimentado,
    sustoObservado: this.props.tarjeta.sustoObservado,
    impactoAmbiente: this.props.tarjeta.impactoAmbiente,
    tareaRealizada: this.props.tarjeta.tareaRealizada,
    responsable: this.props.tarjeta.responsable,
    accionesComplementarias: this.props.tarjeta.accionesComplementarias,
    causa: this.props.tarjeta.causa,
    riesgoFinal: this.props.tarjeta.riesgoFinal,
    tipoAccion: this.props.tarjeta.tipoAccion,
  };

  componentDidUpdate(prevProps) {
    const { error } = this.props;
    if (error !== prevProps.error) {
      //Check for login error
      console.log(error.msg.msg);
      if (error.id === "EDITAR_TARJETA_ERROR") {
        this.setState({
          msg: error.msg.msg,
        });
      } else {
        this.setState({
          msg: null,
        });
      }
    }
  }

  toggle = () => {
    // Clear errors
    this.props.clearErrors();
    this.setState({
      modal: !this.state.modal,
    });
  };
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  onSubmit = (e) => {
    e.preventDefault();
    const {
      _id,
      descripcion,
      detecto,
      prioridad,
      maquina,
      familia,
      equipo,
      sustoExperimentado,
      sustoObservado,
      impactoAmbiente,
      sugerencia,
      tipodeRiesgo,
      riesgoInicial,
      responsable,
      tareaRealizada,
      riesgoFinal,
      tipoAccion,
      accionesComplementarias,
    } = this.state;

    const tarjetaEditada = {
      _id,
      descripcion,
      detecto,
      prioridad,
      maquina,
      familia,
      equipo,
      sustoExperimentado,
      sustoObservado,
      impactoAmbiente,
      sugerencia,
      tipodeRiesgo,
      riesgoInicial,
      responsable,
      tareaRealizada,
      riesgoFinal,
      tipoAccion,
      accionesComplementarias,
    };
    this.props.editarTarjeta(tarjetaEditada);
    this.toggle();
  };

  render() {
    return (
      <div>
        <Button onClick={this.toggle}>Editar Tarjeta</Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>Editar Tarjeta</ModalHeader>
          <ModalBody>
            {this.state.msg ? (
              <Alert color="danger">{this.state.msg}</Alert>
            ) : null}
            <h4>Tarjeta Frente</h4>
            <Form className="mt-3" onSubmit={this.onSubmit} id="agregartarjeta">
              <Row className="my-4">
                <Col>
                  <FormGroup check>
                    <Label check>
                      <Input
                        type="checkbox"
                        id="sustoExperimentado"
                        name="sustoExperimentado"
                        onChange={(e) => {
                          this.onChange({
                            target: {
                              name: e.target.name,
                              value: e.target.checked,
                            },
                          });
                        }}
                      />
                      Reporte de Incidente "Susto" experimentado
                    </Label>
                  </FormGroup>
                </Col>
                <Col>
                  <FormGroup check>
                    <Label check>
                      <Input
                        type="checkbox"
                        id="sustoObservado"
                        defaultValue={this.props.tarjeta.sustoObservado}
                        name="sustoObservado"
                        onChange={(e) => {
                          this.onChange({
                            target: {
                              name: e.target.name,
                              value: e.target.checked,
                            },
                          });
                        }}
                      />
                      Reporte de Incidente "Susto" observado
                    </Label>
                  </FormGroup>
                </Col>
                <Col>
                  <FormGroup check>
                    <Label check>
                      <Input
                        type="checkbox"
                        id="impactoAmbiente"
                        defaultValue={this.state.impactoAmbiente}
                        name="impactoAmbiente"
                        onChange={(e) => {
                          this.onChange({
                            target: {
                              name: e.target.name,
                              value: e.target.checked,
                            },
                          });
                        }}
                      />
                      El incidente puede afectar al Medio Ambiente
                    </Label>
                  </FormGroup>
                </Col>
              </Row>
              <Row>
                <Col>
                  <FormGroup>
                    <Label for="maquina">Maquina / Instalación *</Label>
                    <Input
                      type="select"
                      name="maquina"
                      id="maquina"
                      defaultValue={this.state.maquina}
                      onChange={this.onChange}
                    >
                      <option>Seleccionar</option>
                      <option>1</option>
                      <option>2</option>
                      <option>3</option>
                      <option>4</option>
                    </Input>
                  </FormGroup>

                  <FormGroup>
                    <Label for="detecto">Riesgo Inicial *</Label>
                    <Input
                      type="text"
                      defaultValue={this.state.riesgoInicial}
                      name="riesgoInicial"
                      id="riesgoInicial"
                      onChange={this.onChange}
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="detecto">Detectó *</Label>
                    <Input
                      type="text"
                      name="detecto"
                      defaultValue={this.state.detecto}
                      id="detecto"
                      onChange={this.onChange}
                    />
                  </FormGroup>

                  <FormGroup>
                    <Label for="descripcion">Descripción *</Label>
                    <Input
                      type="textarea"
                      name="descripcion"
                      defaultValue={this.state.descripcion}
                      id="descripcion"
                      onChange={this.onChange}
                    />
                  </FormGroup>
                </Col>
                <Col>
                  <FormGroup>
                    <Label for="equipo">Equipo Autonomo*</Label>
                    <Input
                      type="select"
                      name="equipo"
                      defaultValue={this.state.equipo}
                      id="equipo"
                      onChange={this.onChange}
                    >
                      <option>Seleccionar</option>
                      <option>1</option>
                      <option>2</option>
                      <option>3</option>
                      <option>4</option>
                      <option>5</option>
                      <option>6</option>
                      <option>7</option>
                      <option>8</option>
                      <option>9</option>
                      <option>10</option>
                    </Input>
                  </FormGroup>

                  <FormGroup>
                    <Label for="familia">Familia de anomalias *</Label>
                    <Input
                      type="select"
                      name="familia"
                      defaultValue={this.state.familia}
                      id="familia"
                      onChange={this.onChange}
                    >
                      <option>Seleccionar</option>
                      <option>1 - Pequeñas Deficiencia</option>
                      <option>2 - Condiciones basicas</option>
                      <option>3 - Puntos inaccesibles</option>
                      <option>4 - Focos de contaminacion</option>
                      <option>5 - Defecto de Calidad</option>
                      <option>6 - Elementos Innecesarios</option>
                      <option>7 - Lugares Inseguros</option>
                    </Input>
                  </FormGroup>

                  <FormGroup>
                    <Label for="detecto">Tipo *</Label>
                    <Input
                      type="select"
                      name="tipodeRiesgo"
                      id="tipodeRiesgo"
                      defaultValue={this.state.tipodeRiesgo}
                      onChange={this.onChange}
                    >
                      <option>Seleccionar</option>
                      <option>Atrapamiento</option>
                      <option>Corte</option>
                      <option>Quemadura</option>
                      <option>Deslizamiento</option>
                      <option>Salpicadura</option>
                      <option>Otros</option>
                    </Input>
                  </FormGroup>
                  <FormGroup>
                    <Label for="prioridad">Prioridad *</Label>
                    <Input
                      type="select"
                      name="prioridad"
                      defaultValue={this.state.prioridad}
                      id="prioridad"
                      onChange={this.onChange}
                    >
                      <option>Seleccionar</option>
                      <option>Alta</option>
                      <option>Media</option>
                      <option>Baja</option>
                    </Input>
                  </FormGroup>
                </Col>
              </Row>

              {this.props.tarjeta.estado === "Cerrada" && (
                <div>
                  <h4>Tarjeta Dorso</h4>{" "}
                  <FormGroup>
                    <Label for="responsable">Responsable</Label>
                    <Input
                      onChange={this.onChange}
                      type="text"
                      name="responsable"
                      id="responsable"
                      className="mb-2"
                      defaultValue={this.state.responsable}
                    ></Input>

                    <Label for="responsable">Riesgo Final</Label>
                    <Input
                      onChange={this.onChange}
                      type="text"
                      name="riesgoFinal"
                      id="riesgoFinal"
                      className="mb-2"
                      defaultValue={this.state.riesgoFinal}
                    ></Input>

                    <Label for="updaters">Tiempo empleado en horas</Label>
                    <Input
                      onChange={this.onChange}
                      type="text"
                      name="tiempoEmpleado"
                      id="tiempoEmpleado"
                      defaultValue={this.state.tiempoEmpleado}
                      className="mb-2"
                    ></Input>

                    <Label for="updaters">Causa de la Anomalia</Label>
                    <Input
                      onChange={this.onChange}
                      type="text"
                      name="causa"
                      id="causa"
                      defaultValue={this.state.causa}
                      className="mb-2"
                    ></Input>

                    <Label for="updaters">Tarea Realizada</Label>
                    <Input
                      onChange={this.onChange}
                      type="text"
                      defaultValue={this.state.tareaRealizada}
                      name="tareaRealizada"
                      id="tareaRealizada"
                      className="mb-2"
                    ></Input>

                    <Label for="tipoAccion">Tipo de acción *</Label>
                    <Input
                      type="select"
                      name="tipoAccion"
                      id="tipoAccion"
                      className="mb-2"
                      defaultValue={this.state.tipoAccion}
                      onChange={this.onChange}
                    >
                      <option>Seleccionar</option>
                      <option>Eliminar</option>
                      <option>Contener</option>
                      <option>Reemplazar</option>
                      <option>Simplificar</option>
                    </Input>

                    <Label for="updaters">Material Utilizado</Label>
                    <Input
                      onChange={this.onChange}
                      type="text"
                      name="materialUtilizado"
                      defaultValue={this.state.materialUtilizado}
                      id="materialUtilizado"
                      className="mb-2"
                    ></Input>
                  </FormGroup>
                </div>
              )}

              {this.state.msg ? (
                <Alert color="danger" className="mt-3">
                  {this.state.msg}
                </Alert>
              ) : null}

              <Row className="mt-3">
                <Col>
                  <Button>Subir</Button>
                </Col>
              </Row>
            </Form>
          </ModalBody>
        </Modal>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  error: state.error,
});
export default connect(mapStateToProps, {
  clearErrors,
  editarTarjeta,
})(EditarTarjetaModalAmarilla);
