import React, { Component } from "react";

import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
  Container,
  Row,
  Col,
} from "reactstrap";

class AñadirTarjeta extends Component {
  state = {
    numero,
    descripcion,
    color,
    detecto,
    prioridad,
    familia,
    maquina,
    equipo,
    msg: null,
  };

  componentDidMount() {
    this.props.getTeams();
  }

  componentDidUpdate(prevProps) {
    const { error } = this.props;
    if (error !== prevProps.error) {
      //Check for login error
      console.log(error.msg.msg);
      if (error.id === "CREATE_TICKET_FAIL") {
        this.setState({
          msg: error.msg.msg,
        });
      } else {
        this.setState({
          msg: null,
        });
      }
    }
  }
  toggle = () => {
    // Clear errors
    this.props.clearErrors();
    this.setState({
      modal: !this.state.modal,
    });
  };
  onChange = (e) => {
    if (e.target.value !== "Select Option") {
      this.setState({ [e.target.name]: e.target.value });
    }
  };
  onSubmit = (e) => {
    e.preventDefault();
    const {
      summary,
      description,
      priority,
      assignedTeam,
      submitter,
      project,
      type,
    } = this.state;

    // Create usre object
    const newTicket = {
      summary,
      description,
      priority,
      assignedTeam,
      submitter,
      project,
      type,
    };
    this.props.createTicket(newTicket);
    if (
      summary === "" ||
      description === "" ||
      submitter === "" ||
      priority === "" ||
      assignedTeam === "" ||
      project === "" ||
      type === ""
    ) {
      console.log("Error");
    } else {
      this.toggle();
    }
  };
  render() {
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <h2 className="my-2">Añadir nueva tarjeta</h2>
              <Form className="mt-4">
                <Row>
                  <Col>
                    <FormGroup>
                      <Label for="exampleSelect">Prioridad *</Label>
                      <Input type="select" name="select" id="exampleSelect">
                        <option>Alta</option>
                        <option>Media</option>
                        <option>Baja</option>
                      </Input>
                    </FormGroup>
                  </Col>
                  <Col>
                    <FormGroup>
                      <Label for="text">Maquina *</Label>
                      <Input type="text" name="text" id="text" />
                    </FormGroup>

                    <FormGroup>
                      <Label for="exampleSelect">Prioridad *</Label>
                      <Input type="select" name="select" id="exampleSelect">
                        <option>Alta</option>
                        <option>Media</option>
                        <option>Baja</option>
                      </Input>
                    </FormGroup>

                    <FormGroup>
                      <Label for="examplePassword">Descripcion *</Label>
                      <Input
                        type="text"
                        name="text"
                        id="examplePassword"
                        bsSize="lg"
                      />
                    </FormGroup>
                  </Col>
                  <Col>
                    <FormGroup>
                      <Label for="text">Equipo *</Label>
                      <Input type="text" name="text" id="text" />
                    </FormGroup>

                    <FormGroup>
                      <Label for="exampleSelect">Tipos de anormalidad *</Label>
                      <Input type="select" name="select" id="exampleSelect">
                        <option>1 - Pequeñas Deficiencia</option>
                        <option>2 - Condiciones basicas</option>
                        <option>3 - Puntos inaccesibles</option>
                        <option>4 - Focos de contaminacion</option>
                        <option>5 - Defecto de Calidad</option>
                        <option>6 - Elementos Innecesarios</option>
                        <option>7 - Lugares Inseguros</option>
                      </Input>
                    </FormGroup>
                    <FormGroup>
                      <Label for="examplePassword">Detecto *</Label>
                      <Input type="text" name="text" id="examplePassword" />
                    </FormGroup>
                  </Col>
                </Row>
                <Row className="mt-3">
                  <Col>
                    <Button>Subir</Button>
                  </Col>
                </Row>
              </Form>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
export default AñadirTarjeta;
