import React, { Component } from "react";
import { Row, Col, Container } from "reactstrap";
import { connect } from "react-redux";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import { getKaizen } from "../../store/actions/kaizenActions";
import {
  CerradaDetalle,
  TextDetail,
  AbiertaDetalle,
  ImagenDetalle,
  PlanificacionDetalle,
} from ".";

class LayoutDetalle extends Component {
  componentDidMount() {
    this.props.getTarjetas();
    this.props.getKaizen();
    window.scrollTo(0, 0);
  }
  render() {
    const { tarjetas } = this.props.tarjetas;
    const { tarjetaskaizen } = this.props.tarjetaskaizen;
    const link_id = this.props.location.pathname.substr(9);
    const estadoTarjeta = tarjetas
      .filter(({ _id }) => _id === link_id)
      .map(({ estado }) => estado);

    const estadoTarjetaKaizen = tarjetaskaizen
      .filter(({ _id }) => _id === link_id)
      .map(({ estado }) => estado);

    const plan =
      tarjetas &&
      tarjetas
        .filter(({ _id }) => _id === link_id)
        .map(({ planificacion }) => planificacion);

    const imagen =
      tarjetas &&
      tarjetas
        .filter(({ _id }) => _id === link_id)
        .map(({ imagenUrl }) => imagenUrl);

    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container className="container-fluid">
              {estadoTarjeta.toString() === "Abierta" ? (
                <div>
                  <Row>
                    <Col>
                      <TextDetail
                        tarjetas={tarjetas}
                        link_id={link_id}
                        tarjetaskaizen={tarjetaskaizen}
                      ></TextDetail>
                    </Col>
                  </Row>
                  <Row>
                    <Col sm={6} lg={6}>
                      <AbiertaDetalle
                        className="text-center"
                        tarjetas={tarjetas}
                        tarjetaskaizen={tarjetaskaizen}
                        link_id={link_id}
                      ></AbiertaDetalle>
                    </Col>
                  </Row>

                  {plan[0] && (
                    <Row>
                      <Col>
                        <PlanificacionDetalle
                          tarjetas={tarjetas}
                          link_id={link_id}
                        ></PlanificacionDetalle>
                      </Col>
                    </Row>
                  )}
                  {imagen[0] && (
                    <Row>
                      <Col>
                        <ImagenDetalle
                          tarjetas={tarjetas}
                          link_id={link_id}
                        ></ImagenDetalle>
                      </Col>
                    </Row>
                  )}
                </div>
              ) : (
                <div>
                  <Row>
                    <Col>
                      <TextDetail
                        tarjetas={tarjetas}
                        link_id={link_id}
                        tarjetaskaizen={tarjetaskaizen}
                      ></TextDetail>
                    </Col>
                  </Row>

                  <Row>
                    <Col sm={6} lg={6}>
                      <AbiertaDetalle
                        tarjetas={tarjetas}
                        tarjetaskaizen={tarjetaskaizen}
                        link_id={link_id}
                      ></AbiertaDetalle>
                    </Col>

                    <Col sm={6} lg={6}>
                      <CerradaDetalle
                        tarjetas={tarjetas}
                        link_id={link_id}
                        tarjetaskaizen={tarjetaskaizen}
                      ></CerradaDetalle>
                    </Col>
                  </Row>
                  {plan[0] && (
                    <Row>
                      <Col>
                        <PlanificacionDetalle
                          tarjetas={tarjetas}
                          link_id={link_id}
                        ></PlanificacionDetalle>
                      </Col>
                    </Row>
                  )}
                  {imagen[0] && (
                    <Row>
                      <Col>
                        <ImagenDetalle
                          tarjetas={tarjetas}
                          link_id={link_id}
                        ></ImagenDetalle>
                      </Col>
                    </Row>
                  )}
                </div>
              )}
              {estadoTarjetaKaizen.toString() === "Abierta" ? (
                <div>
                  <Row>
                    <Col>
                      <TextDetail
                        tarjetas={tarjetas}
                        link_id={link_id}
                        tarjetaskaizen={tarjetaskaizen}
                      ></TextDetail>
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <AbiertaDetalle
                        className="text-center"
                        tarjetas={tarjetas}
                        tarjetaskaizen={tarjetaskaizen}
                        link_id={link_id}
                      ></AbiertaDetalle>
                    </Col>
                  </Row>
                  <Row>
                    <Col sm={6} lg={6}>
                      <AbiertaDetalle
                        className="text-center"
                        tarjetas={tarjetas}
                        tarjetaskaizen={tarjetaskaizen}
                        link_id={link_id}
                      ></AbiertaDetalle>
                    </Col>
                  </Row>

                  {plan[0] && (
                    <Row>
                      <Col>
                        <PlanificacionDetalle
                          tarjetas={tarjetas}
                          link_id={link_id}
                        ></PlanificacionDetalle>
                      </Col>
                    </Row>
                  )}
                  {imagen[0] && (
                    <Row>
                      <Col>
                        <ImagenDetalle
                          tarjetas={tarjetas}
                          link_id={link_id}
                        ></ImagenDetalle>
                      </Col>
                    </Row>
                  )}
                </div>
              ) : (
                <div>
                  <Row>
                    <Col>
                      <TextDetail
                        tarjetas={tarjetas}
                        link_id={link_id}
                        tarjetaskaizen={tarjetaskaizen}
                      ></TextDetail>
                    </Col>
                  </Row>

                  <Row>
                    <Col sm={6} lg={6}>
                      <AbiertaDetalle
                        tarjetas={tarjetas}
                        tarjetaskaizen={tarjetaskaizen}
                        link_id={link_id}
                      ></AbiertaDetalle>
                    </Col>

                    <Col sm={6} lg={6}>
                      <CerradaDetalle
                        tarjetas={tarjetas}
                        link_id={link_id}
                        tarjetaskaizen={tarjetaskaizen}
                      ></CerradaDetalle>
                    </Col>
                  </Row>
                  {plan[0] && (
                    <Row>
                      <Col>
                        <PlanificacionDetalle
                          tarjetas={tarjetas}
                          link_id={link_id}
                        ></PlanificacionDetalle>
                      </Col>
                    </Row>
                  )}
                  {imagen[0] && (
                    <Row>
                      <Col>
                        <ImagenDetalle
                          tarjetas={tarjetas}
                          link_id={link_id}
                        ></ImagenDetalle>
                      </Col>
                    </Row>
                  )}
                </div>
              )}
            </Container>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
    tarjetaskaizen: state.tarjetaskaizen,
  };
};
export default connect(mapStateToProps, { getTarjetas, getKaizen })(
  LayoutDetalle
);
