import React, { useState } from "react";
import { Collapse, Button, CardBody, Card, Table } from "reactstrap";
import moment from "moment";
import { Link } from "react-router-dom";
const TableModal = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const toggle = () => setIsOpen(!isOpen);

  return (
    <div>
      <Button color="primary" onClick={toggle} style={{ marginBottom: "1rem" }}>
        Mostrar tabla
      </Button>
      <Collapse isOpen={isOpen}>
        <Card>
          <CardBody>
            <Table className="no-wrap v-middle" responsive>
              <thead>
                <tr className="border-0">
                  {props.fechas.map((item) => {
                    return <th className="border-0">{item}</th>;
                  })}
                </tr>
              </thead>
              {props.tarjetas &&
                props.tarjetas.map(
                  ({
                    numero,
                    color,
                    prioridad,
                    equipo,
                    fecha,
                    descripcion,
                    estado,
                    _id,
                  }) => {
                    const timeDiferrence = moment().diff(fecha, "days");
                    return (
                      <tbody key={_id}>
                        <tr>
                          <td>
                            <div className="d-flex no-block align-items-center">
                              <div className="">
                                <Link to={{ pathname: `/tarjeta/${_id}` }}>
                                  <h5 className="mb-0 font-16 font-medium">
                                    {numero}
                                  </h5>
                                </Link>
                              </div>
                            </div>
                          </td>
                          {color === "Azul" ? (
                            <td>
                              <li className="border-0 p-0 text-info list-inline-item">
                                <i className="fa fa-circle"></i> {color}
                              </li>
                            </td>
                          ) : null}
                          {color === "Roja" ? (
                            <td>
                              <li className="border-0 p-0 text-danger list-inline-item">
                                <i className="fa fa-circle"></i> {color}
                              </li>
                            </td>
                          ) : null}
                          {color === "Verde" ? (
                            <td>
                              <li className="border-0 p-0 text-success list-inline-item">
                                <i className="fa fa-circle"></i> {color}
                              </li>
                            </td>
                          ) : null}
                          {color === "Amarilla" ? (
                            <td>
                              <li className="border-0 p-0 text-warning list-inline-item">
                                <i className="fa fa-circle"></i> {color}
                              </li>
                            </td>
                          ) : null}

                          <td>
                            {prioridad === "Alta" && timeDiferrence >= 15 && (
                              <li className="border-0 p-0 text-danger list-inline-item">
                                <i className="fa fa-circle">
                                  <div>&nbsp;</div>
                                </i>
                              </li>
                            )}
                            {prioridad === "Media" && timeDiferrence >= 30 && (
                              <li className="border-0 p-0 text-danger list-inline-item">
                                <i className="fa fa-circle">
                                  <div>&nbsp;</div>
                                </i>
                              </li>
                            )}
                            {prioridad === "Baja" && timeDiferrence >= 60 && (
                              <li className="border-0 p-0 text-danger list-inline-item">
                                <i className="fa fa-circle">
                                  <div>&nbsp;</div>
                                </i>
                              </li>
                            )}
                            {prioridad}
                          </td>

                          <td>{estado}</td>
                        </tr>
                      </tbody>
                    );
                  }
                )}
            </Table>
          </CardBody>
        </Card>
      </Collapse>
    </div>
  );
};

export default TableModal;
