import React, { Component } from "react";

import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Container,
  Row,
  Col,
} from "reactstrap";

class AñadirTarjeta extends Component {
  state = {
    numero: "1",
    descripcion: "",
    color: "",
    detecto: "",
    prioridad: "",
    familia: "",
    maquina: "",
    equipo: "",
    msg: null,
  };

  componentDidUpdate(prevProps) {
    const { error } = this.props;
    if (error !== prevProps.error) {
      // Check login error
      console.log(error.msg.msg);
      if (error.id === "AGREGAR_TARJETA_ERROR") {
        this.setState({
          msg: error.msg.msg,
        });
      } else {
        this.setState({
          msg: null,
        });
      }
    }
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  onSubmit = (e) => {
    e.preventDefault();
    const {
      numero,
      descripcion,
      color,
      detecto,
      prioridad,
      familia,
      maquina,
      equipo,
    } = this.state;

    // Crear Tarjeta
    const nuevaTarjeta = {
      numero,
      descripcion,
      color,
      detecto,
      prioridad,
      familia,
      maquina,
      equipo,
    };
    console.log(nuevaTarjeta);
  };
  render() {
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <h2 className="my-2">Añadir nueva tarjeta</h2>
              <Form className="mt-4" onSubmit={this.onSubmit}>
                <Row>
                  <Col>
                    <FormGroup>
                      <Label for="descripcion">Descripcion *</Label>
                      <Input
                        type="textarea"
                        name="descripcion"
                        id="descripcion"
                        onChange={this.onChange}
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col>
                    <FormGroup>
                      <Label for="maquina">Maquina *</Label>
                      <Input
                        type="text"
                        name="maquina"
                        id="maquina"
                        onChange={this.onChange}
                      />
                    </FormGroup>

                    <FormGroup>
                      <Label for="prioridad">Prioridad *</Label>
                      <Input
                        type="select"
                        name="prioridad"
                        id="prioridad"
                        onChange={this.onChange}
                      >
                        <option>Seleccionar</option>
                        <option>Alta</option>
                        <option>Media</option>
                        <option>Baja</option>
                      </Input>
                    </FormGroup>

                    <FormGroup>
                      <Label for="color">Color *</Label>
                      <Input type="select" name="color" id="color">
                        <option>Seleccionar</option>
                        <option>Roja</option>
                        <option>Azul</option>
                        <option>Verde</option>
                        <option>Amarilla</option>
                      </Input>
                    </FormGroup>
                  </Col>
                  <Col>
                    <FormGroup>
                      <Label for="equipo">Equipo *</Label>
                      <Input type="text" name="equipo" id="equipo" />
                    </FormGroup>

                    <FormGroup>
                      <Label for="familia">Tipos de anormalidad *</Label>
                      <Input type="select" name="familia" id="familia">
                        <option>Seleccionar</option>
                        <option>1 - Pequeñas Deficiencia</option>
                        <option>2 - Condiciones basicas</option>
                        <option>3 - Puntos inaccesibles</option>
                        <option>4 - Focos de contaminacion</option>
                        <option>5 - Defecto de Calidad</option>
                        <option>6 - Elementos Innecesarios</option>
                        <option>7 - Lugares Inseguros</option>
                      </Input>
                    </FormGroup>
                    <FormGroup>
                      <Label for="detecto">Detecto *</Label>
                      <Input type="text" name="detecto" id="detecto" />
                    </FormGroup>
                  </Col>
                </Row>
                <Row className="mt-3">
                  <Col>
                    <Button>Subir</Button>
                  </Col>
                </Row>
              </Form>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
export default AñadirTarjeta;
