import React, { Component } from "react";
import { Row, Col, Container } from "reactstrap";
import { connect } from "react-redux";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import { CerradaDetalle, TextDetail, AbiertaDetalle, ImagenDetalle } from ".";

class LayoutDetalle extends Component {
  componentDidMount() {
    this.props.getTarjetas();
    window.scrollTo(0, 0);
  }
  render() {
    const { tarjetas } = this.props.tarjetas;
    const link_id = this.props.location.pathname.substr(9);
    const estadoTarjeta = tarjetas
      .filter(({ _id }) => _id === link_id)
      .map(({ estado }) => estado);

    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container className="container-fluid">
              {estadoTarjeta.toString() === "Abierta" ? (
                <div>
                  <Row>
                    <Col>
                      <TextDetail
                        tarjetas={tarjetas}
                        link_id={link_id}
                      ></TextDetail>
                    </Col>
                  </Row>
                  <Row>
                    <Col sm={6} lg={6}>
                      <AbiertaDetalle
                        tarjetas={tarjetas}
                        link_id={link_id}
                      ></AbiertaDetalle>
                    </Col>

                    <Col sm={6} lg={6}>
                      <ImagenDetalle
                        tarjetas={tarjetas}
                        link_id={link_id}
                      ></ImagenDetalle>
                    </Col>
                  </Row>
                </div>
              ) : (
                <div>
                  <Row>
                    <Col>
                      <TextDetail
                        tarjetas={tarjetas}
                        link_id={link_id}
                      ></TextDetail>
                    </Col>
                  </Row>
                  <div class="container-fluid content-row">
                    <div class="row">
                      <div class="col-sm-12 col-lg-6">
                        <div class="card h-100">
                          <AbiertaDetalle
                            tarjetas={tarjetas}
                            link_id={link_id}
                          ></AbiertaDetalle>
                        </div>
                      </div>
                      <CerradaDetalle
                        tarjetas={tarjetas}
                        link_id={link_id}
                      ></CerradaDetalle>
                    </div>
                  </div>

                  <div class="container-fluid">
                    <div class="row">
                      <div class="col-sm d-flex">
                        <div class="card card-body flex-fill">
                          "Lorem ipsum dolor sit amet, consectetur adipiscing
                          elit, sed do eiusmod tempor incididunt ut labore et
                          dolore magna aliqua. Ut enim ad minim veniam, quis
                          nostrud exercitation ullamco laboris nisi ut aliquip
                          ex ea commodo consequat. Duis aute irure dolor in
                          reprehenderit in voluptate velit esse cillum dolore eu
                          fugiat nulla pariatur. Excepteur sint occaecat
                          cupidatat non proident, sunt in culpa qui officia
                          deserunt mollit anim id est laborum."
                        </div>
                      </div>
                      <div class="col-sm d-flex">
                        <div class="card card-body flex-fill">
                          Another small card content.
                        </div>
                      </div>
                    </div>
                  </div>
                  <Row>
                    <Col>
                      <ImagenDetalle
                        tarjetas={tarjetas}
                        link_id={link_id}
                      ></ImagenDetalle>
                    </Col>
                  </Row>
                </div>
              )}
            </Container>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
  };
};
export default connect(mapStateToProps, { getTarjetas })(LayoutDetalle);
