import React, { Component, useState } from "react";
import { connect } from "react-redux";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import { loadUser } from "../../store/actions/authActions";
import { Card, CardBody, Row, Col, Table, Button, Container } from "reactstrap";
import moment from "moment";
import { Link, Redirect } from "react-router-dom";
import InfiniteScroll from "react-infinite-scroll-component";

const useSortableData = (items, config = null) => {
  const [sortConfig, setSortConfig] = React.useState(config);

  const sortedItems = React.useMemo(() => {
    let sortableItems = [...items];
    if (sortConfig !== null) {
      sortableItems.sort((a, b) => {
        if (a[sortConfig.key] < b[sortConfig.key]) {
          return sortConfig.direction === "ascending" ? -1 : 1;
        }
        if (a[sortConfig.key] > b[sortConfig.key]) {
          return sortConfig.direction === "ascending" ? 1 : -1;
        }
        return 0;
      });
    }
    return sortableItems;
  }, [items, sortConfig]);

  const requestSort = (key) => {
    let direction = "ascending";
    if (
      sortConfig &&
      sortConfig.key === key &&
      sortConfig.direction === "ascending"
    ) {
      direction = "descending";
    }
    setSortConfig({ key, direction });
  };

  return { items: sortedItems, requestSort, sortConfig };
};

export const TarjetasTable = (props) => {
  const { items, requestSort, sortConfig } = useSortableData(props.tarjetas);
  const getClassNamesFor = (name) => {
    if (!sortConfig) {
      return;
    }
    return sortConfig.key === name ? sortConfig.direction : undefined;
  };

  const [count, setCount] = useState({
    prev: 0,
    next: 100,
  });
  const [hasMore, setHasMore] = useState(true);
  const [current, setCurrent] = useState(items.slice(count.prev, count.next));
  const getMoreData = () => {
    if (current.length === items.length) {
      setHasMore(false);
      return;
    }
    setTimeout(() => {
      setCurrent(
        current.concat(items.slice(count.prev + 100, count.next + 100))
      );
    }, 2000);
    setCount((prevState) => ({
      prev: prevState.prev + 100,
      next: prevState.next + 100,
    }));
  };

  return (
    <div>
      <div className="page-wrapper d-block">
        <div className="page-content container-fluid">
          <Container>
            <Row>
              <Col>
                <div className="d-sm-flex align-items-center">
                  <div className="">
                    <div>
                      <h2 className="mb-3">Tarjetas</h2>
                    </div>

                    <div className="ml-auto d-sm-flex no-block align-items-center mb-3 d-sm-flex">
                      <div className="dl">
                        <h5 className="font-weight-normal">
                          Todas las tarjetas en la base de datos
                        </h5>
                      </div>
                    </div>
                  </div>
                  <div className="ml-auto d-sm-flex no-block align-items-center mb-3">
                    <Col>
                      <Link to="/agregartarjeta">
                        <Button color="success" className="btn">
                          Agregar Tarjeta
                        </Button>
                      </Link>
                    </Col>
                  </div>
                </div>
              </Col>
            </Row>

            <Card>
              <CardBody>
                <Table className="no-wrap v-middle" responsive>
                  <thead>
                    <tr className="border-0">
                      <th className="border-0">
                        N
                        <Button
                          type="button"
                          onClick={() => requestSort("numero")}
                          className={getClassNamesFor("numero")}
                          color="link"
                        ></Button>
                      </th>
                      <th className="border-0">
                        Color
                        <Button
                          type="button"
                          onClick={() => requestSort("color")}
                          className={getClassNamesFor("color")}
                          color="link"
                        ></Button>
                      </th>
                      <th className="border-0">
                        Equipo autonomo
                        <Button
                          type="button"
                          onClick={() => requestSort("equipo")}
                          className={getClassNamesFor("equipo")}
                          color="link"
                        ></Button>
                      </th>
                      <th className="border-0">
                        Prioridad
                        <Button
                          type="button"
                          onClick={() => requestSort("prioridad")}
                          className={getClassNamesFor("prioridad")}
                          color="link"
                        ></Button>
                      </th>

                      <th className="border-0">
                        Fecha apertura
                        <Button
                          type="button"
                          onClick={() => requestSort("fecha")}
                          color="link"
                          className={getClassNamesFor("fecha")}
                        ></Button>
                      </th>
                      <th className="border-0">
                        Descripcion anomalia
                        <Button
                          type="button"
                          onClick={() => requestSort("descripcion")}
                          color="link"
                          className={getClassNamesFor("descripcion")}
                        ></Button>
                      </th>
                      <th className="border-0">
                        Estado actual
                        <Button
                          type="button"
                          onClick={() => requestSort("estado")}
                          color="link"
                          className={getClassNamesFor("estado")}
                        ></Button>
                      </th>
                    </tr>
                  </thead>
                  {current &&
                    current.map(
                      ({
                        numero,
                        color,
                        prioridad,
                        equipo,
                        fecha,
                        descripcion,
                        estado,
                        _id,
                      }) => {
                        const timeDiferrence = moment().diff(fecha, "days");
                        return (
                          <tbody key={_id}>
                            <tr>
                              <td>
                                <div className="d-flex no-block align-items-center">
                                  <div className="">
                                    <Link to={{ pathname: `/tarjeta/${_id}` }}>
                                      <h5 className="mb-0 font-16 font-medium">
                                        {numero}
                                      </h5>
                                    </Link>
                                  </div>
                                </div>
                              </td>
                            </tr>
                          </tbody>
                        );
                      }
                    )}
                </Table>
                <InfiniteScroll
                  dataLength={current.length}
                  next={getMoreData}
                  hasMore={hasMore}
                  loader={<h4>Cargando...</h4>}
                ></InfiniteScroll>
                {tarjetas &&
                  tarjetas
                    .filter(({ _id }) => _id === link_id)
                    .map(
                      ({
                        estado,
                        color,
                        detecto,
                        numero,
                        descripcion,
                        prioridad,
                        familia,
                        fecha,
                        maquina,
                        parteMaquina,
                        equipo,
                        sugerencia,
                        tipodeRiesgo,
                        riesgoInicial,
                        sustoExperimentado,
                        sustoObservado,
                        impactoAmbiente,
                        planta,
                        photo,
                      }) => {
                        return (
                          <div>
                            {color === "Azul" && (
                              <div class="trapezoidAzul"></div>
                            )}
                            {color === "Verde" && (
                              <div class="trapezoidVerde"></div>
                            )}
                            {color === "Roja" && (
                              <div class="trapezoidRojo"></div>
                            )}
                            {color === "Amarilla" && (
                              <div class="trapezoidAmarilla"></div>
                            )}

                            <Card className="card">
                              <CardBody>
                                <Row className="ml-2 my-1">
                                  <h5
                                    className="font-16 font-medium mr-2"
                                    style={{ display: "block" }}
                                  >
                                    Maquina / Instalacin:
                                  </h5>
                                  <div>&nbsp;</div>
                                  <h5 className="font-16 font-weight-normal">
                                    {maquina}
                                  </h5>
                                </Row>
                                <Row className="ml-2 my-1">
                                  <h5
                                    className="font-16 font-medium mr-2"
                                    style={{ display: "block" }}
                                  >
                                    Parte de maquina:
                                  </h5>
                                  <div>&nbsp;</div>
                                  <h5 className="font-16 font-weight-normal">
                                    {parteMaquina}
                                  </h5>
                                </Row>
                                <Row className="ml-2 my-1">
                                  <h5 className="font-16 font-medium mr-2">
                                    Equipo Autonomo:
                                  </h5>
                                  <h5 className="font-14 font-weight-normal">
                                    {equipo}
                                  </h5>
                                </Row>
                                <Row className="ml-2 my-1">
                                  <h5 className="font-16 font-medium mr-2">
                                    Fecha apertura:
                                  </h5>
                                  <h5 className="font-14 font-weight-normal">
                                    {moment(fecha).format("DD/MM/YYYY LTS ")}
                                  </h5>
                                </Row>
                                <Row className="ml-2 my-1">
                                  <h5 className="font-16 font-medium mr-2">
                                    Detecto:{" "}
                                  </h5>
                                  <h5 className="font-14 font-weight-normal">
                                    {detecto}
                                  </h5>
                                </Row>
                                <Row className="ml-2 my-1">
                                  <h5 className="font-16 font-medium mr-2">
                                    Prioridad:{" "}
                                  </h5>
                                  <h5 className="font-14 font-weight-normal">
                                    {prioridad}
                                  </h5>
                                </Row>
                                <Row className="ml-2 my-1">
                                  <h5 className="font-16 font-medium mr-2">
                                    Familia:
                                  </h5>
                                  <h5 className="font-14 font-weight-normal">
                                    {familia}
                                  </h5>
                                </Row>
                                <Row className="ml-2 my-1">
                                  {color === "Amarilla" ? (
                                    <h5 className="font-16 font-medium mr-2">
                                      Tipo de Riesgo:
                                    </h5>
                                  ) : (
                                    <h5 className="font-16 font-medium mr-2">
                                      Tipo:{" "}
                                    </h5>
                                  )}
                                  <h5 className="font-14 font-weight-normal">
                                    {tipodeRiesgo}
                                  </h5>
                                </Row>

                                <Row className="ml-2 my-1">
                                  <h5 className="font-16 font-medium mr-2">
                                    Riesgo Inicial:
                                  </h5>
                                  <h5 className="font-14 font-weight-normal">
                                    {riesgoInicial}
                                  </h5>
                                </Row>
                                {color === "Amarilla" && (
                                  <Row className="ml-2 my-1">
                                    <h5 className="font-16 font-medium mr-2">
                                      Sugerencia para que no se repita:
                                    </h5>
                                    <h5 className="font-14 font-weight-normal">
                                      {sugerencia}
                                    </h5>
                                  </Row>
                                )}

                                {color === "Amarilla" && (
                                  <div>
                                    <Row className="ml-2 my-1">
                                      <h5 className="font-16 font-medium mr-2">
                                        Reporte de Incidente "Susto"
                                        experimentado:
                                      </h5>
                                      {sustoExperimentado ? (
                                        <h5 className="font-14 font-weight-normal">
                                          Si
                                        </h5>
                                      ) : (
                                        <h5 className="font-14 font-weight-normal">
                                          No
                                        </h5>
                                      )}
                                    </Row>
                                    <Row className="ml-2 my-1">
                                      <h5 className="font-16 font-medium mr-2">
                                        Reporte de Incidente "Susto" observado
                                      </h5>
                                      {sustoObservado ? (
                                        <h5 className="font-14 font-weight-normal">
                                          Si
                                        </h5>
                                      ) : (
                                        <h5 className="font-14 font-weight-normal">
                                          No
                                        </h5>
                                      )}
                                    </Row>
                                    <Row className="ml-2 my-1">
                                      <h5 className="font-16 font-medium mr-2">
                                        El incidente puede afectar al Medio
                                        Ambiente
                                      </h5>
                                      {impactoAmbiente ? (
                                        <h5 className="font-14 font-weight-normal">
                                          Si
                                        </h5>
                                      ) : (
                                        <h5 className="font-14 font-weight-normal">
                                          No
                                        </h5>
                                      )}
                                    </Row>

                                    <Row className="ml-2 my-1">
                                      {estado === "Abierta" && (
                                        <div>
                                          <h5 className="font-16 font-medium">
                                            Alerta
                                          </h5>
