import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import {
  Card,
  CardBody,
  CardTitle,
  CardSubtitle,
  Input,
  Table,
  Col,
  Row,
  Button,
  Form,
  FormGroup,
} from "reactstrap";
import { connect } from "react-redux";
import {
  agregarComentario,
  getTarjetas,
} from "../../../store/actions/tarjetaActions";
import moment from "moment";

export class PlanificacionDetalle extends Component {
  state = {
    descripcion: "",
    autor: "",
  };
  componentDidMount() {
    this.props.getTarjetas();
  }
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  onSubmit = (e) => {
    e.preventDefault();
    const { descripcion, autor } = this.state;
    const link = this.props.location.pathname;
    const completeLink = link.replace("/tarjeta/", "");

    const nuevoComentario = {
      _id: completeLink,
      comentario: {
        autor,
        descripcion,
        fecha: Date.now(),
      },
    };

    if (descripcion === "" || autor === "") {
      console.log("Error");
    } else {
      this.props.agregarComentario(nuevoComentario);
      document.getElementById("form").reset();
    }
  };
  render() {
    const { tarjetas } = this.props.tarjetas;
    const { link_id } = this.props;
    const link = this.props.location.pathname;
    const completeLink = link.replace("/tarjeta/", "");
    console.log(tarjetas);
    return (
      <div>
        <Card>
          <CardBody>
            <div className="d-flex align-items-center">
              <div className="">
                <h3 className="mb-3">
                  Planificación del Tratamiento de Tarjetas
                </h3>
              </div>
            </div>

            {tarjetas &&
              tarjetas
                .filter(({ _id }) => _id === completeLink)
                .map(({ comentarios }) => {
                  return (
                    <div>
                      <Row className="my-2">
                        <Col sm={3}>
                          <h5 className="font-16 font-medium">Status</h5>
                          <h5 className="font-14 font-weight-normal">Status</h5>
                        </Col>
                        <Col sm={3}>
                          <h5 className="font-16 font-medium">Priority</h5>
                          <h5 className="font-14 font-weight-normal">Status</h5>
                        </Col>
                        <Col sm={3}>
                          <h5 className="font-16 font-medium">Status</h5>
                          <h5 className="font-14 font-weight-normal">Status</h5>
                        </Col>
                        <Col sm={3}>
                          <h5 className="font-16 font-medium">Priority</h5>
                          <h5 className="font-14 font-weight-normal">Status</h5>
                        </Col>
                      </Row>
                      <Row className="my-2">
                        <Col sm={3}>
                          <h5 className="font-16 font-medium">Status</h5>
                          <h5 className="font-14 font-weight-normal">Status</h5>
                        </Col>
                        <Col sm={3}>
                          <h5 className="font-16 font-medium">Priority</h5>
                          <h5 className="font-14 font-weight-normal">Status</h5>
                        </Col>
                        <Col sm={3}>
                          <h5 className="font-16 font-medium">Status</h5>
                          <h5 className="font-14 font-weight-normal">Status</h5>
                        </Col>
                        <Col sm={3}>
                          <h5 className="font-16 font-medium">Priority</h5>
                          <h5 className="font-14 font-weight-normal">Status</h5>
                        </Col>
                      </Row>
                      <Row className="my-2">
                        <Col>
                          <h5 className="font-16 font-medium">Status</h5>
                          <h5 className="font-14 font-weight-normal">Status</h5>
                        </Col>
                      </Row>
                    </div>
                  );
                })}

            <hr className="mt-3"></hr>
            <Form onSubmit={this.onSubmit} id="form">
              <Row className="mb-1">
                <Col lg={8}>
                  <FormGroup>
                    <Input
                      onChange={this.onChange}
                      type="text"
                      name="descripcion"
                      id="descripcion"
                      placeholder="Añadir nuevo comentario"
                    />
                  </FormGroup>
                </Col>
                <Col lg={2}>
                  <FormGroup>
                    <Input
                      onChange={this.onChange}
                      type="text"
                      name="autor"
                      id="autor"
                      placeholder="Autor"
                    />
                  </FormGroup>
                </Col>
                <Col lg={2}>
                  <Button className="bg-success border-success">Subir</Button>
                </Col>
              </Row>
            </Form>
          </CardBody>
        </Card>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
  };
};
export default connect(mapStateToProps, { agregarComentario, getTarjetas })(
  withRouter(PlanificacionDetalle)
);
