import { GET_KAIZEN, AGREGAR_KAIZEN, CARGANDO_KAIZEN } from "./types";

import { returnErrors } from "./errorActions";
import axios from "axios";

export const getKAIZEN = () => (dispatch) => {
  dispatch(cargandoKaizen());
  axios
    .get("/api/tarjetaskaizen")
    .then((res) =>
      dispatch({
        type: GET_KAIZEN,
        payload: res.data,
      })
    )
    .catch((err) =>
      dispatch(returnErrors(err.response.data, err.response.status))
    );
};

export const agregarKaizen = (kaizen) => (dispatch, getState) => {
  axios
    .post("/api/tarjetaskaizen", kaizen)
    .then((res) =>
      dispatch({
        type: AGREGAR_KAIZEN,
        payload: res.data,
      })
    )
    .catch((err) =>
      dispatch(
        returnErrors(
          err.response.data,
          err.response.status,
          "AGREGAR_KAIZEN_ERROR"
        )
      )
    );
};
export const cargandoKaizen = () => {
  return {
    type: CARGANDO_KAIZEN,
  };
};
