import React, { Component } from "react";
import { connect } from "react-redux";
import { clearErrors } from "../../../store/actions/errorActions";
import { cerrarTarjeta } from "../../../store/actions/tarjetaActions";
import { getCampos } from "../../../store/actions/camposActions";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  Label,
  Input,
  FormGroup,
  Alert,
} from "reactstrap";
export class PlanificacionModal extends Component {
  state = {
    modal: false,
    _id: this.props._id,
    previstaCierre: "",
    responsableSeguimiento: "",
    recursos: "",
    materiales: "",
    solicitudCompras: "",
    comprometidaCompras: "",
    tareaaRealizar: "",
    responsableTarea: "",
    comentario1: "",
    comentario2: "",
    comentario3: "",
    msg: null,
  };
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  componentDidUpdate(prevProps) {
    const { error } = this.props;
    if (error !== prevProps.error) {
      //Check for login error
      console.log(error.msg.msg);
      if (error.id === "PLANIFICACION_TARJETA_ERROR") {
        this.setState({
          msg: error.msg.msg,
        });
      } else {
        this.setState({
          msg: null,
        });
      }
    }
  }
  toggle = () => {
    // Clear errors
    this.props.clearErrors();
    this.setState({
      modal: !this.state.modal,
    });
  };
  onSubmit = (e) => {
    e.preventDefault();
    const {
      _id,
      previstaCierre,
      responsableSeguimiento,
      recursos,
      materiales,
      solicitudCompras,
      comprometidaCompras,
      tareaaRealizar,
      responsableTarea,
      comentario1,
      comentario2,
      comentario3,
    } = this.state;

    const horaInicio = inicioReparacionHora.split(":");
    const horaFinal = finReparacionHora.split(":");

    const InicioReparacionHoraFloat = parseFloat(horaInicio[0]);
    const FinReparacionHoraFloat = parseFloat(horaFinal[0]);

    const horaFinal1 = FinReparacionHoraFloat - InicioReparacionHoraFloat;

    const tiemploEmpleadoString = `${horaFinal1}`;

    // Cerrar Tarjeta
    const tarjetaActualizada = {
      _id,
      inicioReparacion: inicioReparacionDia + " " + inicioReparacionHora,
      finReparacion: finReparacionDia + " " + finReparacionHora,
      responsable,
      tiempoEmpleado: tiemploEmpleadoString,
      causa,
      tareaRealizada,
      tipoAccion,
      riesgoFinal,
      materialUtilizado,
      convertida,
    };

    this.props.cerrarTarjeta(tarjetaActualizada);

    if (
      !_id ||
      !inicioReparacionDia ||
      !finReparacionDia ||
      !inicioReparacionHora ||
      !finReparacionHora ||
      !responsable ||
      !tiempoEmpleado ||
      !causa ||
      !riesgoFinal ||
      !tipoAccion ||
      !tareaRealizada ||
      !materialUtilizado
    ) {
      return null;
    } else {
      return this.toggle();
    }
  };
  componentDidMount() {
    this.props.getCampos();
  }
  render() {
    const { campos } = this.props.campos;

    return (
      <div>
        <p
          onClick={this.toggle}
          className="my-3 mx-3 text-dark"
          style={{ cursor: "pointer" }}
        >
          Planificacion Tarjeta
        </p>
        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>
            Planificación del Tratamiento de Tarjetas
          </ModalHeader>
          <ModalBody>
            <Form onSubmit={this.onSubmit}>
              <FormGroup>
                <Label for="updaters">Fecha prevista de cierre</Label>
                <Input
                  onChange={this.onChange}
                  type="date"
                  name="previstaCierre"
                  id="previstaCierre"
                  className="mb-2"
                ></Input>

                <Label for="responsable">Responsable del seguimiento</Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="responsableSeguimiento"
                  id="responsableSeguimiento"
                  className="mb-2"
                ></Input>
                <Label for="responsable">Recursos a utilizar</Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="recursos"
                  id="recursos"
                  className="mb-2"
                ></Input>

                <Label for="responsable">Materiales/Repuestos necesarios</Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="materiales"
                  id="materiales"
                  className="mb-2"
                ></Input>

                <Label for="updaters">Fecha solicitud a Compras</Label>
                <Input
                  onChange={this.onChange}
                  type="date"
                  name="solicitudCompras"
                  id="solicitudCompras"
                  className="mb-2"
                ></Input>

                <Label for="updaters">Fecha comprometida por Compras</Label>
                <Input
                  onChange={this.onChange}
                  type="date"
                  name="comprometidaCompras"
                  id="comprometidaCompras"
                  className="mb-2"
                ></Input>

                <Label for="responsable">Tarea a realizar</Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="tareaRealizar"
                  id="tareaRealizar"
                  className="mb-2"
                ></Input>

                <Label for="responsable">
                  Responsible de la tarea a realizar
                </Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="responsableTarea"
                  id="responsableTarea"
                  className="mb-2"
                ></Input>

                <Label for="responsable">
                  Comentario 1/Fecha del comentario
                </Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="comentario1"
                  id="comentario1"
                  className="mb-2"
                ></Input>

                <Label for="responsable">
                  Comentario 2/Fecha del comentario
                </Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="comentario2"
                  id="comentario2"
                  className="mb-2"
                ></Input>

                <Label for="responsable">
                  Comentario 3/Fecha del comentario
                </Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="comentario3"
                  id="comentario3"
                  className="mb-2"
                ></Input>

                {this.state.msg ? (
                  <Alert color="danger" className="mt-3">
                    {this.state.msg}
                  </Alert>
                ) : null}
                <Button color="dark" block style={{ marginTop: "2rem" }}>
                  Subir
                </Button>
              </FormGroup>
            </Form>
          </ModalBody>
        </Modal>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  error: state.error,
  campos: state.campos,
});
export default connect(mapStateToProps, {
  clearErrors,
  cerrarTarjeta,
  getCampos,
})(PlanificacionModal);
