import React, { Component } from "react";
import { connect } from "react-redux";
import {
  Card,
  CardBody,
  Row,
  Col,
  Table,
  Button,
  Container,
  FormGroup,
  Label,
  Input,
} from "reactstrap";
import { Link, Redirect } from "react-router-dom";
import CampoModal from "./camposModal";
import CamposParteMaquinaModal from "./camposparteMaquinaModal";
import { getUsers, borrarUser } from "../../store/actions/usersActions";
import { borrarCampo } from "../../store/actions/camposActions";
import {
  getCampos,
  parteMaquinaDelete,
} from "../../store/actions/camposActions";
export class AdminUsuarios extends Component {
  componentDidMount() {
    this.props.getUsers();
    this.props.getCampos();
  }
  onDeleteClick = (id) => {
    this.props.borrarUser(id);
    this.props.history.push("/admin");
  };
  onDeleteClickCampo = (id) => {
    this.props.borrarCampo(id);
    this.props.history.push("/admin");
  };
  state = {
    idMaquina: "",
    idEquipo: "",
    idTipo: "",
    maquina: "",
    parteMaquina: "",
  };
  parteMaquinaDeleteFunction() {
    const parteMaquinaD = {
      _id: this.state.idMaquina,
      name: this.state.parteMaquina,
    };
    console.log(parteMaquinaD);
  }
  render() {
    const { users } = this.props.users;
    const { campos } = this.props.campos;
    console.log(this.state);
    console.log(campos);
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            {this.props.user && this.props.user.role === "Admin" && (
              <Container>
                <Row>
                  <Col>
                    <div className="d-sm-flex align-items-center">
                      <div className="">
                        <div>
                          <h2 className="mb-3">Administrar</h2>
                        </div>
                      </div>

                      <div className="ml-auto d-sm-flex no-block align-items-center mb-3">
                        <Col>
                          <Link to="/tarjetasfiltro">
                            <Button color="secondary" className="btn">
                              Filtrar Tarjetas
                            </Button>
                          </Link>
                        </Col>
                      </div>
                    </div>
                  </Col>
                </Row>
                <hr className="mb-3"></hr>
                <Row className="mt-3">
                  <Col>
                    <div className="d-sm-flex align-items-center mt-3">
                      <div className="">
                        <div>
                          <h3 className="mb-3">Usuarios</h3>
                        </div>
                      </div>

                      <div className="ml-auto d-sm-flex no-block align-items-center mb-3">
                        <Col>
                          <Link to="/register">
                            <Button color="success" className="btn">
                              Agregar usuario
                            </Button>
                          </Link>
                        </Col>
                      </div>
                    </div>
                  </Col>
                </Row>

                <Card className="mb-3">
                  <CardBody>
                    <Table className="no-wrap v-middle" responsive>
                      <thead>
                        <tr className="border-0">
                          <th className="border-0">Email</th>
                          <th className="border-0">Rol</th>
                          <th className="border-0">Acciones</th>
                        </tr>
                      </thead>
                      {users &&
                        users.map(({ name, email, role, _id }) => {
                          return (
                            <tbody key={_id}>
                              <tr>
                                <td>{email}</td>
                                <td>{role}</td>
                                <td>
                                  <Button
                                    onClick={this.onDeleteClick.bind(this, _id)}
                                    className="bg-danger border-danger"
                                  >
                                    Borrar
                                  </Button>
                                </td>
                              </tr>
                            </tbody>
                          );
                        })}
                    </Table>
                  </CardBody>
                </Card>
                <Row className="mt-3">
                  <Col className="mt-3">
                    <div className="d-sm-flex align-items-center">
                      <div className="">
                        <div>
                          <h3 className="mb-3">Campos</h3>
                        </div>
                      </div>
                    </div>
                  </Col>
                </Row>
                <Card>
                  <CardBody>
                    <Row>
                      <Col>
                        <div className="d-sm-flex align-items-center">
                          <div className="">
                            <div>
                              <FormGroup>
                                <Label for="maquina">
                                  Maquina / Instalaci√≥n
                                </Label>
                                <Input
                                  type="select"
                                  name="maquina"
                                  id="maquina"
                                  onChange={(e) => {
                                    this.setState({
                                      idMaquina: e.target.value,
                                      maquina: e.target.value,
                                    });
                                  }}
                                >
                                  <option>Seleccionar</option>
                                  {campos &&
                                    campos
                                      .filter(({ name }) => {
                                        return name === "maquina";
                                      })
                                      .map(({ name, value, _id }) => {
                                        return (
                                          <option value={_id} name={value}>
                                            {value}
                                          </option>
                                        );
                                      })}
                                </Input>
                              </FormGroup>
                            </div>
                          </div>
                          <div className="ml-auto d-sm-flex no-block align-items-center mb-3">
                            <CampoModal campo="maquina"></CampoModal>
                            <Button
                              onClick={this.onDeleteClickCampo.bind(
                                this,
                                this.state.idMaquina
                              )}
                              className="bg-danger border-danger ml-3"
                            >
                              Borrar
                            </Button>
                          </div>
                        </div>
                      </Col>
                    </Row>
                    {this.state.maquina !== "" && (
                      <Row>
                        <Col>
                          <div className="d-sm-flex align-items-center">
                            <div className="">
                              <div>
                                <FormGroup>
                                  <Label for="maquina">Parte de maquina</Label>
                                  <Input
                                    type="select"
                                    name="parteMaquina"
                                    id="parteMaquina"
                                    onChange={this.onChange}
                                  >
                                    <option>Seleccionar</option>
                                    {campos &&
                                      campos
                                        .filter(({ _id }) => {
                                          return _id === this.state.maquina;
                                        })
                                        .map(({ parteMaquina }) => {
                                          return parteMaquina.map((item) => {
                                            return <option>{item}</option>;
                                          });
                                        })}
                                  </Input>
                                </FormGroup>
                              </div>
                            </div>
                            <div className="ml-auto d-sm-flex no-block align-items-center mb-3">
                              <CamposParteMaquinaModal campo="maquina"></CamposParteMaquinaModal>
                              <Button
                                onClick={this.parteMaquinaDeleteFunction}
                                className="bg-danger border-danger ml-3"
                              >
                                Borrar
                              </Button>
                            </div>
                          </div>
                        </Col>
                      </Row>
                    )}
                    <Row>
                      <Col>
                        <div className="d-sm-flex align-items-center">
                          <div className="">
                            <div>
                              <FormGroup>
                                <Label for="tipo">Tipo</Label>
                                <Input
                                  type="select"
                                  name="tipo"
                                  id="tipo"
                                  onChange={(e) => {
                                    this.setState({
                                      idTipo: e.target.value,
                                    });
                                  }}
                                >
                                  <option>Seleccionar</option>
                                  {campos &&
                                    campos
                                      .filter(({ name }) => {
                                        return name === "tipo";
                                      })
                                      .map(({ name, value, _id }) => {
                                        return (
                                          <option value={_id}>{value}</option>
                                        );
                                      })}
                                </Input>
                              </FormGroup>
                            </div>
                          </div>
                          <div className="ml-auto d-sm-flex no-block align-items-center mb-3">
                            <CampoModal campo="tipo"></CampoModal>
                            <Button
                              onClick={this.onDeleteClickCampo.bind(
                                this,
                                this.state.idTipo
                              )}
                              className="bg-danger border-danger ml-3"
                            >
                              Borrar
                            </Button>
                          </div>
                        </div>
                      </Col>
                    </Row>
                    <Row>
                      <Col>
                        <div className="d-sm-flex align-items-center">
                          <div className="">
                            <div>
                              <FormGroup>
                                <Label for="equipo">Equipo</Label>
                                <Input
                                  type="select"
                                  name="equipo"
                                  id="equipo"
                                  onChange={(e) => {
                                    this.setState({
                                      idEquipo: e.target.value,
                                    });
                                  }}
                                >
                                  <option>Seleccionar</option>
                                  {campos &&
                                    campos
                                      .filter(({ name }) => {
                                        return name === "equipo";
                                      })
                                      .map(({ name, value, _id }) => {
                                        return (
                                          <option value={_id}>{value}</option>
                                        );
                                      })}
                                </Input>
                              </FormGroup>
                            </div>
                          </div>
                          <div className="ml-auto d-sm-flex no-block align-items-center mb-3">
                            <CampoModal campo="equipo"></CampoModal>
                            <Button
                              onClick={this.onDeleteClickCampo.bind(
                                this,
                                this.state.idEquipo
                              )}
                              className="bg-danger border-danger ml-3"
                            >
                              Borrar
                            </Button>
                          </div>
                        </div>
                      </Col>
                    </Row>
                  </CardBody>
                </Card>
              </Container>
            )}
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    users: state.users,
    user: state.auth.user,
    campos: state.campos,
    isAuthenticated: state.auth.isAuthenticated,
  };
};
export default connect(mapStateToProps, {
  getUsers,
  borrarUser,
  getCampos,
  borrarCampo,
  parteMaquinaDelete,
})(AdminUsuarios);
