import React, { Component } from "react";
import CerrarTarjetaModal from "../cerrartarjeta/CerrarTarjetaModal";
import CerrarTarjetaAmarillaModal from "../cerrartarjeta/CerrarTarjetaAmarillaModal";
import QRModal from "./QRModal";
import { withRouter, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { borrarTarjeta } from "../../../store/actions/tarjetaActions";
import { Button, Row, Col } from "reactstrap";
import EditarTarjetaModal from "../editartarjeta/EditarTarjetaModal";
export class TextDetail extends Component {
  onDeleteClick = (id) => {
    this.props.borrarTarjeta(id);
    this.props.history.push("/tarjetas");
  };

  render() {
    const { tarjetas, link_id } = this.props;
    const completeLink = window.location.href.replace(
      `/tarjeta/${link_id}`,
      ""
    );

    return (
      <div>
        {tarjetas &&
          tarjetas
            .filter(({ _id }) => _id === link_id)
            .map(
              ({
                detecto,
                numero,
                descripcion,
                prioridad,
                familia,
                fecha,
                maquina,
                equipo,
                sugerencia,
                tipodeRiesgo,
                riesgoInicial,
                sustoExperimentado,
                sustoObservado,
                impactoAmbiente,
                color,
                estado,
                _id,
                inicioReparacion,
                finReparacion,
                responsable,
                tiempoEmpleado,
                causa,
                tareaRealizada,
                tipoAccion,
                materialUtilizado,
              }) => {
                return (
                  <div className="mb-3 break-text">
                    <Row>
                      <Col>
                        <div className="d-flex align-items-center">
                          <div>
                            <h2 className="mb-3">
                              Tarjeta {color} NÂ° {numero}
                            </h2>
                          </div>

                          <div className="ml-auto d-flex no-block align-items-center">
                            <div className="dl">
                              <Row>
                                {estado === "Abierta" && color !== "Amarilla" && (
                                  <Col>
                                    <CerrarTarjetaModal
                                      _id={link_id}
                                      color={color}
                                    ></CerrarTarjetaModal>
                                  </Col>
                                )}
                                {estado === "Abierta" && color === "Amarilla" && (
                                  <Col>
                                    <CerrarTarjetaAmarillaModal
                                      _id={link_id}
                                      color={color}
                                    ></CerrarTarjetaAmarillaModal>
                                  </Col>
                                )}
                                <Col>
                                  <QRModal
                                    path={this.props.location.pathname}
                                    color={color}
                                    numero={numero}
                                    completePath={completeLink}
                                  >
                                    Mostrar QR
                                  </QRModal>
                                </Col>
                                <Col>
                                  <EditarTarjetaModal
                                    tarjeta={{
                                      numero,
                                      descripcion,
                                      prioridad,
                                      familia,
                                      fecha,
                                      maquina,
                                      estado,
                                      equipo,
                                      sugerencia,
                                      detecto,
                                      tipodeRiesgo,
                                      riesgoInicial,
                                      sustoExperimentado,
                                      sustoObservado,
                                      impactoAmbiente,
                                      color,
                                      inicioReparacion,
                                      finReparacion,
                                      responsable,
                                      tiempoEmpleado,
                                      causa,
                                      tareaRealizada,
                                      tipoAccion,
                                      materialUtilizado,
                                      _id,
                                    }}
                                  ></EditarTarjetaModal>
                                </Col>
                                {localStorage.token && (
                                  <Col>
                                    <Button
                                      color="danger"
                                      onClick={this.onDeleteClick.bind(
                                        this,
                                        _id
                                      )}
                                    >
                                      Borrar Tarjeta
                                    </Button>
                                  </Col>
                                )}
                              </Row>
                            </div>
                          </div>
                        </div>
                      </Col>
                    </Row>
                  </div>
                );
              }
            )}
      </div>
    );
  }
}

export default connect(null, { borrarTarjeta })(withRouter(TextDetail));
