import React, { Component } from "react";
import { connect } from "react-redux";
import { clearErrors } from "../../../store/actions/errorActions";
import {
  cerrarTarjeta,
  getTarjetas,
} from "../../../store/actions/tarjetaActions";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  Label,
  Input,
  FormGroup,
  Alert,
  Col,
  Row,
} from "reactstrap";
export class CerrarTarjetaModal extends Component {
  componentDidMount() {
    this.props.getTarjetas();
  }
  state = {
    modal: false,
    _id: this.props._id,
    inicioReparacionDia: "",
    inicioReparacionHora: "",
    finReparacionDia: "",
    finReparacionHora: "",
    resposable: "",
    tiempoEmpleado: "",
    causa: "",
    tareaRealizada: "",
    materialUtilizado: "",
    msg: null,
  };
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  componentDidUpdate(prevProps) {
    const { error } = this.props;
    if (error !== prevProps.error) {
      //Check for login error
      console.log(error.msg.msg);
      if (error.id === "CERRAR_TARJETA_ERROR") {
        this.setState({
          msg: error.msg.msg,
        });
      } else {
        this.setState({
          msg: null,
        });
      }
    }
  }
  toggle = () => {
    // Clear errors
    this.props.clearErrors();
    this.setState({
      modal: !this.state.modal,
    });
  };
  onSubmit = (e) => {
    e.preventDefault();
    const {
      _id,
      inicioReparacionDia,
      inicioReparacionHora,
      finReparacionDia,
      finReparacionHora,
      responsable,
      tiempoEmpleado,
      causa,
      tareaRealizada,
      materialUtilizado,
    } = this.state;

    // Cerrar Tarjeta
    const tarjetaActualizada = {
      _id,
      inicioReparacion: inicioReparacionDia + " " + inicioReparacionHora,
      finReparacion: finReparacionDia + " " + finReparacionHora,
      responsable,
      tiempoEmpleado,
      causa,
      tareaRealizada,
      materialUtilizado,
    };
    this.props.cerrarTarjeta(tarjetaActualizada);
  };
  render() {
    if (this.props.tarjetas.agregarsuccess) {
      return this.toggle();
    }
    return (
      <div>
        <Button onClick={this.toggle}>Cerrar Tarjeta</Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>Cerrar Tarjeta</ModalHeader>
          <ModalBody>
            <Form onSubmit={this.onSubmit}>
              <FormGroup>
                <Label for="updaters">Inicio Reparacion</Label>
                <Input
                  onChange={this.onChange}
                  type="date"
                  name="inicioReparacionDia"
                  id="inicioReparacionDia"
                  className="mb-2"
                ></Input>
                <Input
                  onChange={this.onChange}
                  type="time"
                  name="inicioReparacionHora"
                  id="inicioReparacionHora"
                  className="mb-2"
                ></Input>

                <Label for="updaters">Fin Reparacion</Label>
                <Input
                  onChange={this.onChange}
                  type="date"
                  name="finReparacionDia"
                  id="finReparacionDia"
                  className="mb-2"
                ></Input>
                <Input
                  onChange={this.onChange}
                  type="time"
                  name="finReparacionHora"
                  id="finReparacionHora"
                  className="mb-2"
                ></Input>

                <Label for="responsable">Responsable</Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="responsable"
                  id="responsable"
                  className="mb-2"
                ></Input>

                <Label for="updaters">Tiempo empleado en horas</Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="tiempoEmpleado"
                  id="tiempoEmpleado"
                  className="mb-2"
                ></Input>

                <Label for="updaters">Causa de la Anomalia</Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="causa"
                  id="causa"
                  className="mb-2"
                ></Input>

                <Label for="updaters">Tarea Realizada</Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="tareaRealizada"
                  id="tareaRealizada"
                  className="mb-2"
                ></Input>

                <Label for="updaters">Material Utilizado</Label>
                <Input
                  onChange={this.onChange}
                  type="text"
                  name="materialUtilizado"
                  id="materialUtilizado"
                  className="mb-2"
                ></Input>

                {this.state.msg ? (
                  <Alert color="danger" className="mt-3">
                    {this.state.msg}
                  </Alert>
                ) : null}
                <Button color="dark" block style={{ marginTop: "2rem" }}>
                  Subir
                </Button>
              </FormGroup>
            </Form>
          </ModalBody>
        </Modal>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  tarjetas: state.tarjetas,
  error: state.error,
});
export default connect(mapStateToProps, { clearErrors, cerrarTarjeta })(
  CerrarTarjetaModal
);
