import React, { Component } from "react";

import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  Label,
  Input,
  FormGroup,
  NavLink,
  Alert,
} from "reactstrap";
import { connect } from "react-redux";
import { agregarFilter } from "../../store/actions/filterActions";

class PresetModal extends Component {
  state = {
    modal: false,
    nombre: "",
    selectedOption,
    numero,
    color,
    equipo,
    prioridad,
    fecha,
    descripcion,
    estado,
    maquina,
    detecto,
    familia,
    qrcode,
  };

  componentDidMount() {
    this.setState({
      selectedOption: this.props.state.selectedOption,
      numero: this.props.state.numero,
      color: this.props.state.color,
      equipo: this.props.state.equipo,
      prioridad: this.props.state.prioridad,
      fecha: this.props.state.fecha,
      descripcion: this.props.state.descripcion,
      estado: this.props.state.estado,
      maquina: this.props.state.maquina,
      detecto: this.props.state.detecto,
      familia: this.props.state.familia,
      qrcode: this.props.state.qrcode,
    });
  }

  toggle = () => {
    this.setState({
      modal: !this.state.modal,
    });
  };
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  onSubmit() {
    const {
      selectedOption,
      numero,
      color,
      equipo,
      prioridad,
      fecha,
      descripcion,
      estado,
      maquina,
      detecto,
      familia,
      qrcode,
    } = this.state;

    // Crear Filter
    const nuevoFilter = {
      nombre: this.state.nombre,
      selectedOption,
      numero,
      color,
      equipo,
      prioridad,
      fecha,
      descripcion,
      estado,
      maquina,
      detecto,
      familia,
      qrcode,
    };

    if (this.state.nombre === "") {
      return null;
    } else {
      return this.props.agregarFilter(nuevoFilter);
    }
  }
  render() {
    if (this.props.filters.agregarsuccessfilter === true) {
      this.toggle();
    }
    return (
      <div>
        <Button className="ml-3" onClick={this.toggle} href="#">
          Saves
        </Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>
            Guardar como preestablecido
          </ModalHeader>
          <ModalBody>
            <Label for="nombre">Nombre</Label>
            <Input
              type="text"
              name="nombre"
              id="nombre"
              placeholder="Nombre"
              className="mb-3"
              onChange={this.onChange}
            />
            <Form onSubmit={this.onSubmit}>
              <Button color="dark" block style={{ marginTop: "2rem" }}>
                Subir
              </Button>
            </Form>
          </ModalBody>
        </Modal>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  filters: state.filters,
});
export default connect(mapStateToProps, { agregarFilter })(PresetModal);
