import React, { useState } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Input,
  Table,
  FormGroup,
  Col,
  Row,
  Form,
} from "reactstrap";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { getTarjetas } from "../../../store/actions/tarjetaActions";

const CynTarjetasModal = (props) => {
  const { buttonLabel, className } = props;

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  const { tarjetas } = props.tarjetas;

  console.log(
    tarjetas &&
      tarjetas
        .filter(({ numero, color }) => color === "Azul" && numero === "62")
        .map(({ estado }) => {
          return estado;
        })
  );

  return (
    <div>
      <p onClick={toggle}>Ver</p>
      <Modal isOpen={modal} toggle={toggle} className={className}>
        <ModalHeader toggle={toggle}>Color y Numero Tarjetas</ModalHeader>
        <ModalBody>
          <Table>
            <thead>
              <tr>
                <th>Numero</th>
                <th>Color</th>
                <th>Estado</th>
                <th></th>
              </tr>
            </thead>

            {props.preTarjeta &&
              props.preTarjeta.map((item, index) => {
                const idTarjeta =
                  tarjetas &&
                  tarjetas
                    .filter(
                      ({ numero, color }) =>
                        color === item.color && numero === item.numero
                    )
                    .map(({ _id }) => {
                      return _id;
                    });
                const link = `/tarjeta/${idTarjeta}`;
                return (
                  <tr>
                    <Link to={link}>
                      <th scope="row">{item.numero}</th>
                    </Link>
                    <td>{item.color}</td>
                    <td>
                      {tarjetas &&
                        tarjetas
                          .filter(
                            ({ numero, color }) =>
                              color === item.color && numero === item.numero
                          )
                          .map(({ estado }) => {
                            return estado;
                          })}
                    </td>
                  </tr>
                );
              })}
            <tr>
              <td>
                <Input
                  className="mt-3"
                  type="number"
                  name="descripcion"
                  id="descripcion"
                  placeholder="Numero"
                ></Input>
              </td>
              <td>
                <Input
                  className="mt-3"
                  type="text"
                  name="descripcion"
                  id="descripcion"
                  placeholder="Color"
                ></Input>
              </td>
              <td>
                <Button className="mt-3">Subir</Button>
              </td>
              <td></td>
            </tr>
            <Form id="form">
              <Row className="mb-1">
                <Col lg={8}>
                  <FormGroup>
                    <Input
                      type="text"
                      name="descripcion"
                      id="descripcion"
                      placeholder="AÃ±adir nuevo comentario"
                    />
                  </FormGroup>
                </Col>
                <Col lg={2}>
                  <FormGroup>
                    <Input
                      type="text"
                      name="autor"
                      id="autor"
                      placeholder="Autor"
                    />
                  </FormGroup>
                </Col>
                <Col lg={2}>
                  <Button className="bg-success border-success">Subir</Button>
                </Col>
              </Row>
            </Form>
          </Table>
        </ModalBody>
      </Modal>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
  };
};
export default connect(mapStateToProps, { getTarjetas })(CynTarjetasModal);
