import React, { Component } from "react";
import { connect } from "react-redux";
import { getTarjetas } from "./store/actions/tarjetaActions";
import { Card, CardBody, Row, Col, Table, Button, Container } from "reactstrap";
import moment from "moment";

const useSortableData = (items, config = null) => {
  const [sortConfig, setSortConfig] = React.useState(config);

  const sortedItems = React.useMemo(() => {
    let sortableItems = [...items];
    if (sortConfig !== null) {
      sortableItems.sort((a, b) => {
        if (a[sortConfig.key] < b[sortConfig.key]) {
          return sortConfig.direction === "ascending" ? -1 : 1;
        }
        if (a[sortConfig.key] > b[sortConfig.key]) {
          return sortConfig.direction === "ascending" ? 1 : -1;
        }
        return 0;
      });
    }
    return sortableItems;
  }, [items, sortConfig]);

  const requestSort = (key) => {
    let direction = "ascending";
    if (
      sortConfig &&
      sortConfig.key === key &&
      sortConfig.direction === "ascending"
    ) {
      direction = "descending";
    }
    setSortConfig({ key, direction });
  };

  return { items: sortedItems, requestSort, sortConfig };
};

export const ProductTable = (props) => {
  const { items, requestSort, sortConfig } = useSortableData(props.tarjetas);
  const getClassNamesFor = (name) => {
    if (!sortConfig) {
      return;
    }
    return sortConfig.key === name ? sortConfig.direction : undefined;
  };

  return (
    <table>
      <caption>Products</caption>
      <thead>
        <tr className="border-0">
          <th className="border-0">
            <button
              type="button"
              onClick={() => requestSort("numero")}
              className={getClassNamesFor("numero")}
            >
              NÂ°
            </button>
          </th>
          <th className="border-0">
            <button
              type="button"
              onClick={() => requestSort("color")}
              className={getClassNamesFor("color")}
            >
              Color
            </button>
          </th>
          <th className="border-0">
            <button
              type="button"
              onClick={() => requestSort("prioridad")}
              className={getClassNamesFor("prioridad")}
            >
              Prioridad
            </button>
          </th>
          <th className="border-0">
            <button
              type="button"
              onClick={() => requestSort("familia")}
              className={getClassNamesFor("familia")}
            >
              Familia
            </button>
          </th>
          <th className="border-0">
            <button
              type="button"
              onClick={() => requestSort("fecha")}
              className={getClassNamesFor("fecha")}
            >
              Fecha apertura
            </button>
          </th>
          <th className="border-0">
            <button
              type="button"
              onClick={() => requestSort("descripcion")}
              className={getClassNamesFor("descripcion")}
            >
              Descripcion Anomalia
            </button>
          </th>
          <th className="border-0">
            <button
              type="button"
              onClick={() => requestSort("estado")}
              className={getClassNamesFor("estado")}
            >
              Estado Actual
            </button>
          </th>
        </tr>
      </thead>
      <thead>
        <tr>
          <th>
            <button
              type="button"
              onClick={() => requestSort("numero")}
              className={getClassNamesFor("numero")}
            >
              Numero
            </button>
          </th>
          <th>
            <button
              type="button"
              onClick={() => requestSort("price")}
              className={getClassNamesFor("price")}
            >
              Price
            </button>
          </th>
          <th>
            <button
              type="button"
              onClick={() => requestSort("stock")}
              className={getClassNamesFor("stock")}
            >
              In Stock
            </button>
          </th>
        </tr>
      </thead>
      <tbody>
        {items.map(({ numero, _id, color, descripcion }) => (
          <tr key={_id}>
            <td>{numero}</td>
            <td>{color}</td>
            <td>{descripcion}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export class ComponentTable extends Component {
  componentDidMount() {
    this.props.getTarjetas();
  }

  render() {
    return (
      <div className="App">
        <ProductTable tarjetas={this.props.tarjetas.tarjetas} />
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
  };
};
export default connect(mapStateToProps, { getTarjetas })(ComponentTable);
