import {
  GET_TARJETAS,
  CARGANDO_TARJETAS,
  AGREGAR_TARJETA,
} from "../actions/types";
const initState = {
  tarjetas: [],
  cargando: false,
};

export default function (state = initState, action) {
  switch (action.type) {
    case GET_TARJETAS:
      return {
        ...state,
        tarjetas: action.payload,
        cargando: false,
      };
    case AGREGAR_TARJETA:
      return {
        ...state,
        tarjetas: [action.payload, ...state.tarjetas],
      };

    case CARGANDO_TARJETAS:
      return {
        ...state,
        cargando: true,
      };
    default:
      return state;
  }
}
