import React, { Component, useState } from "react";
import { connect } from "react-redux";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import { loadUser } from "../../store/actions/authActions";
import { Card, CardBody, Row, Col, Table, Button, Container } from "reactstrap";
import moment from "moment";
import { Link, Redirect } from "react-router-dom";
import { List } from "react-virtualized";

const height = 700;
const rowHeight = 40;
const width = 1000;

const useSortableData = (items, config = null) => {
  const [sortConfig, setSortConfig] = React.useState(config);

  const sortedItems = React.useMemo(() => {
    let sortableItems = [...items];
    if (sortConfig !== null) {
      sortableItems.sort((a, b) => {
        if (a[sortConfig.key] < b[sortConfig.key]) {
          return sortConfig.direction === "ascending" ? -1 : 1;
        }
        if (a[sortConfig.key] > b[sortConfig.key]) {
          return sortConfig.direction === "ascending" ? 1 : -1;
        }
        return 0;
      });
    }
    return sortableItems;
  }, [items, sortConfig]);

  const requestSort = (key) => {
    let direction = "ascending";
    if (
      sortConfig &&
      sortConfig.key === key &&
      sortConfig.direction === "ascending"
    ) {
      direction = "descending";
    }
    setSortConfig({ key, direction });
  };

  return { items: sortedItems, requestSort, sortConfig };
};

export const TarjetasTable = (props) => {
  const { items, requestSort, sortConfig } = useSortableData(props.tarjetas);
  const getClassNamesFor = (name) => {
    if (!sortConfig) {
      return;
    }
    return sortConfig.key === name ? sortConfig.direction : undefined;
  };

  const [count, setCount] = useState({
    prev: 0,
    next: 100,
  });
  const [hasMore, setHasMore] = useState(true);
  const [current, setCurrent] = useState(items.slice(count.prev, count.next));
  const getMoreData = () => {
    if (current.length === items.length) {
      setHasMore(false);
      return;
    }
    setTimeout(() => {
      setCurrent(
        current.concat(items.slice(count.prev + 100, count.next + 100))
      );
    }, 2000);
    setCount((prevState) => ({
      prev: prevState.prev + 100,
      next: prevState.next + 100,
    }));
  };

  const tarjetasRojas = items.filter(({ color }) => color === "Roja");
  const tarjetasVerdes = items.filter(({ color }) => color === "Verde");

  const rowRenderer = ({ index, isScrolling, key, style }) => {
    return (
      <div>
        <div>
          {tarjetasRojas[index].color === "Azul" && (
            <div class="trapezoidAzul"></div>
          )}
          {tarjetasRojas[index].color === "Verde" && (
            <div class="trapezoidVerde"></div>
          )}
          {tarjetasRojas[index].color === "Roja" && (
            <div class="trapezoidRojo"></div>
          )}
          {tarjetasRojas[index].color === "Amarilla" && (
            <div class="trapezoidAmarilla"></div>
          )}

          <Card className="card">
            <CardBody>
              <Row className="my-3 text-center">
                <Col>
                  <h5 className="font-16 font-medium text-center">
                    {tarjetasRojas[index].numero}
                  </h5>
                </Col>
              </Row>
            </CardBody>
            {tarjetasRojas[index].color === "Azul" && (
              <div class="rectangleAzul"></div>
            )}
            {tarjetasRojas[index].color === "Verde" && (
              <div class="rectangleVerde text-center"></div>
            )}
            {tarjetasRojas[index].color === "Roja" && (
              <div class="rectangleRojo"></div>
            )}
            {tarjetasRojas[index].color === "Amarilla" && (
              <div class="rectangleAmarilla"></div>
            )}
          </Card>
        </div>
      </div>
    );
  };

  return (
    <div>
      <div className="page-wrapper d-block">
        <div className="page-content container-fluid">
          <Container>
            <Row>
              <Col>
                <div className="d-sm-flex align-items-center">
                  <div className="">
                    <div>
                      <h2 className="mb-3">Tarjetas</h2>
                    </div>

                    <div className="ml-auto d-sm-flex no-block align-items-center mb-3 d-sm-flex">
                      <div className="dl">
                        <h5 className="font-weight-normal">
                          Todas las tarjetas en la base de datos
                        </h5>
                      </div>
                    </div>
                  </div>
                  <div className="ml-auto d-sm-flex no-block align-items-center mb-3">
                    <Col>
                      <Link to="/agregartarjeta">
                        <Button color="success" className="btn">
                          Agregar Tarjeta
                        </Button>
                      </Link>
                    </Col>
                  </div>
                </div>
              </Col>
            </Row>
            <Row>
              <Col>
                <Card>
                  <CardBody>
                    <Row>
                      {props.tarjetas &&
                        props.tarjetas
                          .filter(({ color }) => color === "Verde")
                          .map(
                            ({
                              estado,
                              color,
                              detecto,
                              numero,
                              descripcion,
                              prioridad,
                              familia,
                              fecha,
                              maquina,
                              parteMaquina,
                              equipo,
                              sugerencia,
                              tipodeRiesgo,
                              riesgoInicial,
                              sustoExperimentado,
                              sustoObservado,
                              impactoAmbiente,
                              planta,
                              photo,
                            }) => {
                              return (
                                <div>
                                  <Col>
                                    <div>
                                      {color === "Azul" && (
                                        <div class="trapezoidAzul"></div>
                                      )}
                                      {color === "Verde" && (
                                        <div class="trapezoidVerde"></div>
                                      )}
                                      {color === "Roja" && (
                                        <div class="trapezoidRojo"></div>
                                      )}
                                      {color === "Amarilla" && (
                                        <div class="trapezoidAmarilla"></div>
                                      )}

                                      <Card className="card">
                                        <CardBody>
                                          <Row className="my-3 text-center">
                                            <Col>
                                              <h5 className="font-16 font-medium text-center">
                                                {numero}
                                              </h5>
                                            </Col>
                                          </Row>
                                        </CardBody>
                                        {color === "Azul" && (
                                          <div class="rectangleAzul"></div>
                                        )}
                                        {color === "Verde" && (
                                          <div class="rectangleVerde text-center"></div>
                                        )}
                                        {color === "Roja" && (
                                          <div class="rectangleRojo"></div>
                                        )}
                                        {color === "Amarilla" && (
                                          <div class="rectangleAmarilla"></div>
                                        )}
                                      </Card>
                                    </div>
                                  </Col>
                                </div>
                              );
                            }
                          )}
                    </Row>
                  </CardBody>
                </Card>
              </Col>
              <Col>
                <List
                  rowCount={items.length}
                  width={width}
                  height={height}
                  rowHeight={rowHeight}
                  rowRenderer={rowRenderer}
                  overscanRowCount={3}
                />
              </Col>
              <Col>
                <Card>
                  <CardBody>
                    <Row>
                      {props.tarjetas &&
                        props.tarjetas
                          .filter(({ color }) => color === "Amarilla")
                          .map(
                            ({
                              estado,
                              color,
                              detecto,
                              numero,
                              descripcion,
                              prioridad,
                              familia,
                              fecha,
                              maquina,
                              parteMaquina,
                              equipo,
                              sugerencia,
                              tipodeRiesgo,
                              riesgoInicial,
                              sustoExperimentado,
                              sustoObservado,
                              impactoAmbiente,
                              planta,
                              photo,
                            }) => {
                              return (
                                <div>
                                  <Col>
                                    <div>
                                      {color === "Azul" && (
                                        <div class="trapezoidAzul"></div>
                                      )}
                                      {color === "Verde" && (
                                        <div class="trapezoidVerde"></div>
                                      )}
                                      {color === "Roja" && (
                                        <div class="trapezoidRojo"></div>
                                      )}
                                      {color === "Amarilla" && (
                                        <div class="trapezoidAmarilla"></div>
                                      )}

                                      <Card className="card">
                                        <CardBody>
                                          <Row className="my-3 text-center">
                                            <Col>
                                              <h5 className="font-16 font-medium text-center">
                                                {numero}
                                              </h5>
                                            </Col>
                                          </Row>
                                        </CardBody>
                                        {color === "Azul" && (
                                          <div class="rectangleAzul"></div>
                                        )}
                                        {color === "Verde" && (
                                          <div class="rectangleVerde text-center"></div>
                                        )}
                                        {color === "Roja" && (
                                          <div class="rectangleRojo"></div>
                                        )}
                                        {color === "Amarilla" && (
                                          <div class="rectangleAmarilla"></div>
                                        )}
                                      </Card>
                                    </div>
                                  </Col>
                                </div>
                              );
                            }
                          )}
                    </Row>
                  </CardBody>
                </Card>
              </Col>
              <Col>
                <Card>
                  <CardBody>
                    <Row>
                      {props.tarjetas &&
                        props.tarjetas
                          .filter(({ color }) => color === "Azul")
                          .map(
                            ({
                              estado,
                              color,
                              detecto,
                              numero,
                              descripcion,
                              prioridad,
                              familia,
                              fecha,
                              maquina,
                              parteMaquina,
                              equipo,
                              sugerencia,
                              tipodeRiesgo,
                              riesgoInicial,
                              sustoExperimentado,
                              sustoObservado,
                              impactoAmbiente,
                              planta,
                              photo,
                            }) => {
                              return (
                                <div>
                                  <Col>
                                    <div>
                                      {color === "Azul" && (
                                        <div class="trapezoidAzul"></div>
                                      )}
                                      {color === "Verde" && (
                                        <div class="trapezoidVerde"></div>
                                      )}
                                      {color === "Roja" && (
                                        <div class="trapezoidRojo"></div>
                                      )}
                                      {color === "Amarilla" && (
                                        <div class="trapezoidAmarilla"></div>
                                      )}

                                      <Card className="card">
                                        <CardBody>
                                          <Row className="my-3 text-center">
                                            <Col>
                                              <h5 className="font-16 font-medium text-center">
                                                {numero}
                                              </h5>
                                            </Col>
                                          </Row>
                                        </CardBody>
                                        {color === "Azul" && (
                                          <div class="rectangleAzul"></div>
                                        )}
                                        {color === "Verde" && (
                                          <div class="rectangleVerde text-center"></div>
                                        )}
                                        {color === "Roja" && (
                                          <div class="rectangleRojo"></div>
                                        )}
                                        {color === "Amarilla" && (
                                          <div class="rectangleAmarilla"></div>
                                        )}
                                      </Card>
                                    </div>
                                  </Col>
                                </div>
                              );
                            }
                          )}
                    </Row>
                  </CardBody>
                </Card>
              </Col>
            </Row>
          </Container>
        </div>
      </div>
    </div>
  );
};

export class MisTarjetas extends Component {
  componentDidMount() {
    this.props.getTarjetas();
    this.props.loadUser();
    window.scrollTo(0, 0);
  }

  render() {
    if (this.props.isAuthenticated === false && this.props.isLoading === false)
      return <Redirect to="/login" />;

    return (
      <div>
        <TarjetasTable
          tarjetas={this.props.tarjetas.tarjetas}
          user={this.props.user}
        />
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
    isAuthenticated: state.auth.isAuthenticated,
    isLoading: state.auth.isLoading,
    user: state.auth.user,
  };
};
export default connect(mapStateToProps, { getTarjetas, loadUser })(MisTarjetas);
