import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import { Card, CardBody, Row, Col, Table, Button, Container } from "reactstrap";
import moment from "moment";
import Select from "react-select";
const options = [
  { value: "N째", label: "N째" },
  { value: "Color", label: "Color" },
  { value: "Prioridad", label: "Prioridad" },
];
class MisTarjetasFiltro extends Component {
  componentDidMount() {
    this.props.getTarjetas();
  }
  state = {
    selectedOption: null,
  };
  handleChange = (selectedOption) => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption);
  };
  render() {
    const { tarjetas } = this.props.tarjetas;
    const { selectedOption } = this.state;
    console.log(selectedOption);
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <Row>
                <Col>
                  <div>
                    <h2 className="mb-3">Tarjetas</h2>
                  </div>

                  <div className="ml-auto d-flex no-block align-items-center mb-3">
                    <div className="dl">
                      <h5 className="font-weight-normal">Filtrar tarjetas</h5>
                    </div>
                  </div>
                </Col>
              </Row>
              <div className="mb-3">
                <Select
                  value={selectedOption}
                  onChange={this.handleChange}
                  options={options}
                  isMulti
                />
              </div>
              <Card>
                <CardBody>
                  <Table className="no-wrap v-middle" responsive>
                    <thead>
                      <tr className="border-0">
                        {selectedOption &&
                          selectedOption.map(({ value }) => {
                            return <th className="border-0">{value}</th>;
                          })}
                      </tr>
                    </thead>
                    {tarjetas &&
                      tarjetas.map(
                        ({
                          numero,
                          color,
                          prioridad,
                          familia,
                          fecha,
                          descripcion,
                          estado,
                          _id,
                        }) => {
                          return (
                            <tbody key={_id}>
                              <tr>
                                <td>
                                  <div className="d-flex no-block align-items-center">
                                    <div className="">
                                      <Link
                                        to={{ pathname: `/tarjeta/${_id}` }}
                                      >
                                        <h5 className="mb-0 font-16 font-medium">
                                          {numero}
                                        </h5>
                                      </Link>
                                    </div>
                                  </div>
                                </td>
                                {color === "Azul" ? (
                                  <td>
                                    <li className="border-0 p-0 text-info list-inline-item">
                                      <i className="fa fa-circle"></i> {color}
                                    </li>
                                  </td>
                                ) : null}
                                {color === "Roja" ? (
                                  <td>
                                    <li className="border-0 p-0 text-danger list-inline-item">
                                      <i className="fa fa-circle"></i> {color}
                                    </li>
                                  </td>
                                ) : null}
                                {color === "Verde" ? (
                                  <td>
                                    <li className="border-0 p-0 text-success list-inline-item">
                                      <i className="fa fa-circle"></i> {color}
                                    </li>
                                  </td>
                                ) : null}
                                {color === "Amarilla" ? (
                                  <td>
                                    <li className="border-0 p-0 text-warning list-inline-item">
                                      <i className="fa fa-circle"></i> {color}
                                    </li>
                                  </td>
                                ) : null}

                                <td>{prioridad}</td>

                                <td>{familia}</td>
                                <td>{moment(fecha).format("l LTS")}</td>
                                <td>{descripcion}</td>

                                <td>{estado}</td>
                                <td>
                                  <Link to={{ pathname: `/tarjeta/${_id}` }}>
                                    <Button
                                      color="success"
                                      className="btn bg-secondary border border-secondary"
                                    >
                                      Ver detalle
                                    </Button>
                                  </Link>
                                </td>
                              </tr>
                            </tbody>
                          );
                        }
                      )}
                  </Table>
                </CardBody>
              </Card>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
    user: state.auth.user,
  };
};
export default connect(mapStateToProps, { getTarjetas })(MisTarjetasFiltro);
