import {
  GET_TICKETS,
  TICKET_LOADING,
  ADD_TICKET,
  ADD_TICKET_COMMENT,
} from "../actions/types";
const initState = {
  tickets: [],
  loading: false,
};

export default function (state = initState, action) {
  switch (action.type) {
    case GET_TICKETS:
      return {
        ...state,
        tickets: action.payload,
        loading: false,
      };
    case ADD_TICKET:
      return {
        ...state,
        tickets: [action.payload, ...state.tickets],
      };
    case ADD_TICKET_COMMENT:
      return Object.assign({}, state, {
        tickets: state.tickets
          .filter((ticket) => {
            return ticket.title !== action.payload.title; //delete matched data
          })
          .concat(action.payload), //concats new data
      });
    case TICKET_LOADING:
      return {
        ...state,
        loading: true,
      };
    default:
      return state;
  }
}
