import React, { Component } from "react";
import { Card, CardBody, CardTitle, Container } from "reactstrap";
import { Picture } from "react-responsive-image";
export class ImagenDetalle extends Component {
  render() {
    const { tarjetas, link_id } = this.props;

    return (
      <div>
        {tarjetas &&
          tarjetas
            .filter(({ _id }) => _id === link_id)
            .map(({ color, numero }) => {
              return (
                <div>
                  <Card>
                    <CardBody>
                      <h4>Imagen adjuntada</h4>

                      <Picture
                        src={`${process.env.PUBLIC_URL}/uploads/${
                          color + numero
                        }.png`}
                        sizes="(min-width: 36em) 33.3vw, 100vw"
                      />
                    </CardBody>
                  </Card>
                </div>
              );
            })}
      </div>
    );
  }
}

export default ImagenDetalle;
