import React, { Component } from "react";
import { connect } from "react-redux";
import { Card, CardBody, Row, Col, Table, Button, Container } from "reactstrap";
import { Link, Redirect } from "react-router-dom";
import { getUsers } from "../../store/actions/usersActions";
export class AdminUsuarios extends Component {
  componentDidMount() {
    this.props.getUsers();
  }
  render() {
    const { users } = this.props.users;

    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            {this.props.user && this.props.user.role === "Admin" && (
              <Card className="px-5 py-5">
                <form onSubmit={this.onSubmit}>
                  <h3>Registrar Usuario</h3>

                  <div className="form-group">
                    <Label for="email">Email</Label>
                    <Input
                      type="email"
                      name="email"
                      id="email"
                      placeholder="Email"
                      className="mb-3"
                      onChange={this.onChange}
                    />
                  </div>

                  <div className="form-group">
                    <Label for="password">Password</Label>
                    <Input
                      type="password"
                      name="password"
                      id="password"
                      placeholder="Password"
                      className="mb-3"
                      onChange={this.onChange}
                    />
                  </div>
                  <Label for="color">Role</Label>
                  <Input
                    type="select"
                    name="role"
                    id="role"
                    onChange={this.onChange}
                  >
                    <option>Seleccionar</option>
                    <option>Admin</option>
                    <option>Nivel Medio</option>
                    <option>Nivel Bajo</option>
                  </Input>

                  <button
                    type="submit"
                    className="btn btn-primary btn-block mt-3"
                  >
                    Sign Up
                  </button>
                  {this.state.msg ? (
                    <Alert color="danger" className="mt-3">
                      {this.state.msg}
                    </Alert>
                  ) : null}
                </form>
              </Card>
            )}
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    users: state.users,
    user: state.auth.user,
    isAuthenticated: state.auth.isAuthenticated,
  };
};
export default connect(mapStateToProps, { getUsers })(AdminUsuarios);
