import React, { Component } from "react";
import { connect } from "react-redux";
import { clearErrors } from "../../../store/actions/errorActions";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  Label,
  Input,
  FormGroup,
  Alert,
} from "reactstrap";
export class CerrarTarjetaModal extends Component {
  state = {
    modal: false,
    inicioReparacion: Date.now(),
    finReparacion: Date.now(),
    resposable: "",
    tiempoEmpleado: "",
    causa: "",
    tareaRealizada: "",
    materialUtilizado: "",
    msg: null,
  };
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  componentDidUpdate(prevProps) {
    const { error } = this.props;
    if (error !== prevProps.error) {
      //Check for login error
      console.log(error.msg.msg);
      if (error.id === "CERRAR_TARJETA_ERROR") {
        this.setState({
          msg: error.msg.msg,
        });
      } else {
        this.setState({
          msg: null,
        });
      }
    }
  }
  toggle = () => {
    // Clear errors
    this.props.clearErrors();
    this.setState({
      modal: !this.state.modal,
    });
  };
  onSubmit = (e) => {
    e.preventDefault();
    const {
      inicioReparacion,
      finReparacion,
      resposable,
      tiempoEmpleado,
      causa,
      tareaRealizada,
      materialUtilizado,
    } = this.state;

    // Cerrar Tarjeta
    const tarjetaActualizada = {
      inicioReparacion,
      finReparacion,
      resposable,
      tiempoEmpleado,
      causa,
      tareaRealizada,
      materialUtilizado,
    };
    console.log(tarjetaActualizada);
  };
  render() {
    return (
      <div>
        <Button onClick={this.toggle}>Cerrar Tarjeta</Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>Cerrar Tarjeta</ModalHeader>
          <ModalBody>
            <Form onSubmit={this.onSubmit}>
              <FormGroup>
                <Label for="updaters">Inicio Reparacion</Label>
                <Input
                  onChange={this.onChange}
                  type="date"
                  name="inicioReparacion"
                  id="inicioReparacion"
                  className="mb-2"
                ></Input>
                <Input
                  onChange={this.onChange}
                  type="time"
                  name="inicioReparacion"
                  id="inicioReparacion"
                  className="mb-2"
                ></Input>

                {this.state.msg ? (
                  <Alert color="danger" className="mt-3">
                    {this.state.msg}
                  </Alert>
                ) : null}
                <Button color="dark" block style={{ marginTop: "2rem" }}>
                  Subir
                </Button>
              </FormGroup>
            </Form>
          </ModalBody>
        </Modal>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  error: state.error,
});
export default connect(mapStateToProps, { clearErrors })(CerrarTarjetaModal);
