import React, { Component } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  Label,
  Input,
  FormGroup,
  NavLink,
  Alert,
} from "reactstrap";
import { connect } from "react-redux";
import { agregarCampos } from "../../store/actions/camposActions";

class CambiarRolModal extends Component {
  state = {
    modal: false,
    name: this.props.campo,
    value: "",
  };

  toggle = () => {
    this.setState({
      modal: !this.state.modal,
    });
  };
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  onSubmit = (e) => {
    e.preventDefault();
    const { name, value } = this.state;

    // Crear Campo
    const nuevoCampos = {
      name,
      value,
    };
    e.preventDefault();
    this.props.agregarCampos(nuevoCampos);
    if (this.state.value === "") {
      return null;
    } else {
      return this.toggle();
    }
  };

  render() {
    return (
      <div>
        <Button className="ml-3 btn-secondary" onClick={this.toggle} href="#">
          Cambiar Rol
        </Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>Cambiar Rol</ModalHeader>
          <ModalBody>
            <Label for="value">Nuevo rol</Label>
            <Input
              type="text"
              name="value"
              id="value"
              placeholder="Nombre del campo"
              className="mb-3"
              onChange={this.onChange}
            />
            <Input
              type="select"
              name="familia"
              id="familia"
              onChange={this.onChange}
            >
              <option>Seleccionar</option>
              <option>1 - Peque√±as Deficiencia</option>
              <option>2 - Condiciones basicas</option>
              <option>3 - Puntos inaccesibles</option>
              <option>4 - Focos de contaminacion</option>
              <option>5 - Defecto de Calidad</option>
              <option>6 - Elementos Innecesarios</option>
              <option>7 - Lugares Inseguros</option>
            </Input>
            <Form onSubmit={this.onSubmit}>
              <Button color="dark" block style={{ marginTop: "2rem" }}>
                Subir
              </Button>
            </Form>
          </ModalBody>
        </Modal>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  campos: state.campos,
});
export default connect(mapStateToProps, { agregarCampos })(CambiarRolModal);
