import {
  GET_KAIZEN,
  CARGANDO_KAIZEN,
  AGREGAR_KAIZEN,
  AGREGAR_PLANIFICACION,
  AGREGAR_IMAGEN,
} from "../actions/types";
const initState = {
  tarjetaskaizen: [],
  cargando: false,
  agregarsuccess: false,
  tarjetaActualId: "",
};

export default function (state = initState, action) {
  switch (action.type) {
    case GET_KAIZEN:
      return {
        ...state,
        tarjetaskaizen: action.payload,
        cargando: false,
        agregarsuccess: false,
        tarjetaActualId: "",
      };
    case AGREGAR_KAIZEN:
      return {
        ...state,
        tarjetaskaizen: [action.payload, ...state.tarjetas],
        agregarsuccess: true,
        tarjetaActualId: action.payload._id,
      };

    default:
      return state;
  }
}
