import React, { useState } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Card,
  CardBody,
  Row,
  Col,
  Table,
  Container,
} from "reactstrap";
import { Link } from "react-router-dom";
import moment from "moment";

const TableModal = (props) => {
  const { buttonLabel, className } = props;

  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);

  return (
    <div>
      <Button onClick={toggle}>â†“</Button>
      <Modal isOpen={modal} toggle={toggle} className={className}>
        <ModalHeader toggle={toggle}>Tabla de tarjetas</ModalHeader>
        <ModalBody>
          <Card>
            <CardBody>
              <Table className="no-wrap v-middle" responsive>
                <thead>
                  <tr className="border-0">
                    <th className="border-0">Mes</th>
                    {props.fechas.map((item) => {
                      return <th className="border-0">{item}</th>;
                    })}
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>Acumuladas (abiertas)</td>
                    {props.fechas.map((item, index) => {
                      return <td>{props.tarjetasFiltro1[index]}</td>;
                    })}
                  </tr>
                </tbody>
                <tbody>
                  <tr>
                    <td>Acumuladas (cerradas)</td>
                    {props.fechas.map((item, index) => {
                      return <td>{props.tarjetasFiltro2[index]}</td>;
                    })}
                  </tr>
                </tbody>
                <tbody>
                  <tr>
                    <td>Porcentaje (cerradas)</td>
                    {props.fechas.map((item, index) => {
                      const porcentaje =
                        (props.tarjetasFiltro3[index] /
                          props.tarjetasFiltro1[index]) *
                        100;
                      return <td>{porcentaje.substr(0, 3)}%</td>;
                    })}
                  </tr>
                </tbody>
              </Table>
            </CardBody>
          </Card>
        </ModalBody>
      </Modal>
    </div>
  );
};

export default TableModal;
