import React, { Component } from "react";
import QRCode from "qrcode.react";
import { Button } from "reactstrap";
import CerrarTarjetaModal from "../cerrartarjeta/CerrarTarjetaModal";

export class TextDetail extends Component {
  render() {
    const { tarjetas, link_id } = this.props;
    return (
      <div>
        {tarjetas &&
          tarjetas
            .filter(({ _id }) => _id === link_id)
            .map(
              ({
                _id,
                inicioReparacion,
                finReparacion,
                responsable,
                tiempoEmpleado,
                causa,
                tareaRealizada,
                materialUtilizado,
              }) => {
                return (
                  <div className="mb-3">
                    <div className="d-flex align-items-center">
                      <div>
                        <h2 className="mb-3">
                          Tarjeta {color} NÂ° {numero}
                        </h2>
                        <h5 className="font-weight-normal">{descripcion}</h5>
                        {estado === "Cerrado" ? null : (
                          <div className="mb-3 mt-3">
                            <CerrarTarjetaModal
                              _id={link_id}
                            ></CerrarTarjetaModal>
                          </div>
                        )}
                      </div>

                      <div className="ml-auto d-flex no-block align-items-center">
                        <div className="dl">
                          <QRCode
                            size="90"
                            className=""
                            value={`http://localhost:3000/tarjeta/${link_id}`}
                          />
                        </div>
                      </div>
                    </div>
                  </div>
                );
              }
            )}
      </div>
    );
  }
}

export default TextDetail;
