import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import {
  Card,
  CardBody,
  Row,
  Col,
  Table,
  Button,
  Container,
  Input,
  Label,
} from "reactstrap";
import moment from "moment";
import Select from "react-select";
const options = [
  { value: "N°", label: "numero" },
  { value: "Color", label: "color" },
  { value: "Prioridad", label: "prioridad" },
  { value: "Equipo Autonomo", label: "equipo" },
  { value: "Fecha apertura", label: "fecha" },
  { value: "Descripcion anomalia", label: "descripcion" },
  { value: "Estado acutal", label: "estado" },
];
class MisTarjetasFiltro extends Component {
  componentDidMount() {
    this.props.getTarjetas();
  }
  state = {
    selectedOption: null,
    color: null,
    equipo: null,
  };
  handleChange = (selectedOption) => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption);
  };
  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  render() {
    const { tarjetas } = this.props.tarjetas;
    const { selectedOption } = this.state;
    console.log(selectedOption);
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <Row>
                <Col>
                  <div>
                    <h2 className="mb-3">Tarjetas</h2>
                  </div>

                  <div className="ml-auto d-flex no-block align-items-center mb-3">
                    <div className="dl">
                      <h5 className="font-weight-normal">Filtrar tarjetas</h5>
                    </div>
                  </div>
                </Col>
              </Row>
              <div className="mb-3">
                <Select
                  value={selectedOption}
                  onChange={this.handleChange}
                  options={options}
                  isMulti
                />
              </div>
              <div className="mb-3">
                <Label for="color">Color</Label>
                <Input
                  type="select"
                  name="color"
                  id="color"
                  onChange={this.onChange}
                >
                  <option>Seleccionar</option>
                  <option>Azul</option>
                  <option>Roja</option>
                  <option>Amarilla</option>
                  <option>Verde</option>
                </Input>
              </div>
              <Card>
                <CardBody>
                  <Table className="no-wrap v-middle" responsive>
                    <thead>
                      <tr className="border-0">
                        {selectedOption &&
                          selectedOption.map(({ value }) => {
                            return <th className="border-0">{value}</th>;
                          })}
                      </tr>
                    </thead>

                    {this.state.color === null
                      ? tarjetas &&
                        tarjetas.map((item, index) => {
                          return (
                            <tbody key={index}>
                              <tr>
                                {selectedOption &&
                                  selectedOption.map(
                                    ({ value, label }, index) => {
                                      return <td key={index}>{item[label]}</td>;
                                    }
                                  )}
                              </tr>
                            </tbody>
                          );
                        })
                      : tarjetas &&
                        tarjetas
                          .filter(({ color }) => color === this.state.color)
                          .map((item, index) => {
                            return (
                              <tbody key={index}>
                                <tr>
                                  {selectedOption &&
                                    selectedOption.map(
                                      ({ value, label }, index) => {
                                        return (
                                          <td key={index}>{item[label]}</td>
                                        );
                                      }
                                    )}
                                </tr>
                              </tbody>
                            );
                          })}
                  </Table>
                </CardBody>
              </Card>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
    user: state.auth.user,
  };
};
export default connect(mapStateToProps, { getTarjetas })(MisTarjetasFiltro);
