import React, { Component } from "react";
import { connect } from "react-redux";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import { loadUser } from "../../store/actions/authActions";
import moment from "moment";
import {
  Card,
  CardBody,
  Row,
  Col,
  Table,
  Button,
  Container,
  Input,
} from "reactstrap";
import { Link, Redirect } from "react-router-dom";
import HorizontalScroller from "react-horizontal-scroll-container";
export class MisTarjetasTest extends Component {
  render() {
    state = {
      selectedOption: null,

      numero: "",
    };
    var filter = {
      numero: this.state.numero && this.state.numero,
    };
    const multiFilter = (arr, filters) => {
      const filterKeys = Object.keys(filters);
      return arr.filter((eachObj) => {
        return filterKeys.every((eachKey) => {
          if (!filters[eachKey].length) {
            return true; // passing an empty filter means that filter is ignored.
          }
          return filters[eachKey].includes(eachObj[eachKey]);
        });
      });
    };
    const newFilter = multiFilter(tarjetas, filter);
    const { tarjetas } = this.props.tarjetas;
    return (
      <div>
        <div>
          <div className="page-wrapper d-block">
            <div className="page-content container-fluid">
              <Container>
                <Row>
                  <Col>
                    <div className="d-sm-flex align-items-center">
                      <div className="">
                        <div>
                          <h2 className="mb-3">Tarjetas</h2>
                        </div>

                        <div className="ml-auto d-sm-flex no-block align-items-center mb-3 d-sm-flex">
                          <div className="dl">
                            <h5 className="font-weight-normal">
                              Todas las tarjetas en la base de datos
                            </h5>
                          </div>
                        </div>
                      </div>
                      <div className="ml-auto d-sm-flex no-block align-items-center mb-3">
                        <Col>
                          <Link to="/agregartarjeta">
                            <Button color="success" className="btn">
                              Agregar Tarjeta
                            </Button>
                          </Link>
                        </Col>
                      </div>
                    </div>
                  </Col>
                </Row>
                <div className="mt-3">
                  <Row>
                    <Col>
                      <div className="d-sm-flex align-items-center">
                        <div className="">
                          <div>
                            <h3 className="mb-3">Verde</h3>
                          </div>
                        </div>
                        <div className="ml-auto d-sm-flex no-block align-items-center mb-3">
                          <Input
                            type="number"
                            name="numero"
                            id="numero"
                            placeHolder="Filtrar por numero"
                          ></Input>
                        </div>
                      </div>
                    </Col>
                  </Row>
                  <Row className="mt-3">
                    <div className="scroll-box">
                      <div className="scroll-box__wrapper">
                        <div className="scroll-box__container mr-3" role="list">
                          {tarjetas &&
                            tarjetas
                              .filter(
                                ({ numero, color }) =>
                                  numero === "7" && color === "Verde"
                              )
                              .map(
                                (
                                  {
                                    estado,
                                    color,
                                    detecto,
                                    numero,
                                    descripcion,
                                    prioridad,
                                    familia,
                                    fecha,
                                    maquina,
                                    parteMaquina,
                                    equipo,
                                    sugerencia,
                                    tipodeRiesgo,
                                    riesgoInicial,
                                    sustoExperimentado,
                                    sustoObservado,
                                    impactoAmbiente,
                                  },
                                  index
                                ) => {
                                  return (
                                    <div
                                      className="scroll-box__item mr-3"
                                      role="listitem"
                                      key={`scroll-box-item-${index}`}
                                    >
                                      <div class="trapezoidVerde"></div>

                                      <Card className="card123">
                                        <CardBody className="cardBodymargin">
                                          <Row className="ml-2 my-1">
                                            <h5
                                              className="font-16 font-medium mr-2"
                                              style={{ display: "block" }}
                                            >
                                              Numero:
                                            </h5>
                                            <div>&nbsp;</div>
                                            <h5 className="font-16 font-weight-normal">
                                              {numero}
                                            </h5>
                                          </Row>
                                          <Row className="ml-2 my-1">
                                            <h5
                                              className="font-16 font-medium mr-2"
                                              style={{ display: "block" }}
                                            >
                                              Maquina / Instalaci√≥n:
                                            </h5>
                                            <div>&nbsp;</div>
                                            <h5 className="font-16 font-weight-normal">
                                              {maquina}
                                            </h5>
                                          </Row>

                                          <Row className="ml-2 my-1">
                                            <h5 className="font-16 font-medium mr-2">
                                              Equipo Autonomo:
                                            </h5>
                                            <h5 className="font-14 font-weight-normal">
                                              {equipo}
                                            </h5>
                                          </Row>
                                          <Row className="ml-2 my-1">
                                            <h5 className="font-16 font-medium mr-2">
                                              Fecha apertura:
                                            </h5>
                                            <h5 className="font-14 font-weight-normal">
                                              {moment(fecha).format(
                                                "DD/MM/YYYY LTS "
                                              )}
                                            </h5>
                                          </Row>
                                        </CardBody>

                                        <div class="rectangleVerde text-center"></div>
                                      </Card>
                                    </div>
                                  );
                                }
                              )}
                        </div>
                      </div>
                    </div>
                  </Row>
                </div>
              </Container>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
    isAuthenticated: state.auth.isAuthenticated,
    isLoading: state.auth.isLoading,
    user: state.auth.user,
  };
};
export default connect(mapStateToProps, { getTarjetas, loadUser })(
  MisTarjetasTest
);
