import React, { Component } from "react";
import CanvasJSReact from "../canvasjs.react";
var CanvasJSChart = CanvasJSReact.CanvasJSChart;
var CanvasJS = CanvasJSReact.CanvasJS;
export class GraficoRojas extends Component {
  constructor() {
    super();
    this.toggleDataSeries = this.toggleDataSeries.bind(this);
  }
  toggleDataSeries(e) {
    if (typeof e.dataSeries.visible === "undefined" || e.dataSeries.visible) {
      e.dataSeries.visible = false;
    } else {
      e.dataSeries.visible = true;
    }

    this.chart.render();
  }

  render() {
    const { tarjetas } = this.props;

    // Formulas para "Rojas acumuladas abiertas"

    // Filtro todos los meses en el que hay tarjetas abiertas Rojas
    const fechasTarjetasRojas = tarjetas
      .filter(({ estado, color }) => estado === "Abierta" && color === "Rojas")
      .map(({ fecha }) => fecha.substr(0, 7));

    // Borro todos los meses repetidos
    let fechasTarjetasRojas1 = new Set(fechasTarjetasRojas);
    const fechasTarjetasRojasUnicas = [...fechasTarjetasRojas1];

    // Numero total de tarjetas de cada mes (no acumulado)
    let array = fechasTarjetasRojasUnicas.map((item, index) => {
      return (
        tarjetas.filter(
          ({ estado, fecha, color }) =>
            color === "Rojas" &&
            estado === "Abierta" &&
            fecha.substr(4, 4).replace("-", "").replace("-", "") ===
              item.slice(5, 7)
        ).length + 1
      );
    });

    // Acumulado de tarjetas por mes
    function compareDecimals(a, b) {
      if (a === b) return 0;
      return a < b ? -1 : 1;
    }
    const arrTarjetasRojasAcumuladas = array.map((elem, index) =>
      array
        .sort(compareDecimals)
        .slice(0, index + 1)
        .reduce((a, b) => a + b)
    );

    console.log(arrTarjetasRojasAcumuladas);

    // Datos para el grafico
    const RojasAcumuladasAbiertasData = [
      fechasTarjetasRojasUnicas.sort().map((item, index) => {
        return {
          x: new Date(
            parseInt(item.slice(0, 4)),
            parseInt(item.slice(5, 7) - 1)
          ),
          y: arrTarjetasRojasAcumuladas[index],
        };
      }),
    ];

    // Formulas para "Rojas acumuladas cerradas"

    // Filtro todos los meses en el que hay tarjetas cerradas Rojas
    const fechasTarjetasRojasCerradas = tarjetas
      .filter(({ estado, color }) => estado === "Cerrada" && color === "Rojas")
      .map(({ finReparacion }) => finReparacion.substr(0, 7));

    // Borro todos los meses repetidos
    let fechasTarjetasRojas1Cerradas = new Set(fechasTarjetasRojasCerradas);
    const fechasTarjetasRojasUnicasCerradas = [...fechasTarjetasRojas1Cerradas];

    // Numero total de tarjetas de cada mes (no acumulado)
    let arrayCerradas = fechasTarjetasRojasUnicasCerradas.map((item, index) => {
      return tarjetas.filter(
        ({ estado, finReparacion, color }) =>
          color === "Rojas" &&
          estado === "Cerrada" &&
          finReparacion.substr(4, 4).replace("-", "").replace("-", "") ===
            item.slice(5, 7)
      ).length;
    });

    // Acumulado de tarjetas por mes
    const arrTarjetasRojasAcumuladasCerradas = arrayCerradas
      .sort(compareDecimals)
      .map((elem, index) =>
        arrayCerradas.slice(0, index + 1).reduce((aa, bb) => aa + bb)
      );

    // Datos para el grafico
    const RojasAcumuladasAbiertasDataCerradas = [
      fechasTarjetasRojasUnicasCerradas.sort().map((item, index) => {
        return {
          x: new Date(
            parseInt(item.slice(0, 4)),
            parseInt(item.slice(5, 7) - 1)
          ),
          y: arrTarjetasRojasAcumuladasCerradas[index],
        };
      }),
    ];

    // Formulas para "Porcentaje acumuladas cerradas"

    // Borro todos los meses en el que solo hay una abierta o cerradda
    let fechasTarjetasRojasAbiertasCerradas = new Set(
      fechasTarjetasRojasUnicas,
      fechasTarjetasRojasUnicasCerradas
    );
    const fechasTarjetasRojasUnicasCerradasAbiertas = [
      ...fechasTarjetasRojasAbiertasCerradas,
    ];

    // Datos para el grafico de cerradas porcentaje

    const RojasAcumuladasAbiertasDataCerradasPorcentaje = [
      fechasTarjetasRojasUnicasCerradasAbiertas.map((item, index) => {
        return {
          x: new Date(
            parseInt(item.slice(0, 4)),
            parseInt(item.slice(5, 7) - 1)
          ),
          y:
            (arrTarjetasRojasAcumuladasCerradas[index] /
              arrTarjetasRojasAcumuladas[index]) *
            100,
        };
      }),
    ];
    CanvasJS.addCultureInfo("es", {
      decimalSeparator: ",", // Observe ToolTip Number Format
      digitGroupSeparator: ".", // Observe axisY labels

      months: [
        "Enero",
        "Febrero",
        "Marzo",
        "Abril",
        "Marzo",
        "Junio",
        "Julio",
        "Agosto",
        "Septiembre",
        "Octubre",
        "Noviembre",
        "Diciembre",
      ],
    });
    const options = {
      culture: "es",
      animationEnabled: true,
      culture: "es",
      axisX: {
        valueFormatString: "MMMM",

        interval: 1,
        intervalType: "month",
      },
      axisY: {
        title: "Cantidad de tarjetas",
        lineColor: "#000000",
        tickColor: "#000000",
        labelFontColor: "#000000",
      },
      axisY2: {
        title: "% de Cierre",
        suffix: "%",
        lineColor: "#000000",
        tickColor: "#000000",
        labelFontColor: "#000000",
      },
      toolTip: {
        shared: true,
      },
      legend: {
        cursor: "pointer",
        itemclick: this.toggleDataSeries,
        verticalAlign: "top",
      },
      data: [
        {
          color: "#F73C1B",
          type: "column",
          name: "Rojas acumuladas (abiertas)",
          showInLegend: true,
          xValueFormatString: "MMMM YYYY",
          dataPoints: RojasAcumuladasAbiertasData[0],
        },
        {
          type: "line",
          name: "Rojas acumuladas (cerradas)",
          showInLegend: true,
          dataPoints: RojasAcumuladasAbiertasDataCerradas[0],
        },
        {
          type: "line",
          color: "#121212",
          name: "Porcentaje Rojas Cerradas",
          showInLegend: true,
          axisYType: "secondary",
          yValueFormatString: "#,##0",
          dataPoints: RojasAcumuladasAbiertasDataCerradasPorcentaje[0],
        },
      ],
    };

    return (
      <div>
        <CanvasJSChart
          culture="en"
          options={options}
          onRef={(ref) => (this.chart = ref)}
        />
      </div>
    );
  }
}

export default GraficoRojas;
