import React from "react";
import { render } from "react-dom";
import { makeData, Logo, Tips } from "./Utils";

// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";

const columns = [
  {
    Header: "Name",
    columns: [
      {
        Header: "First Name",
        accessor: "firstName",
      },
      {
        Header: "Last Name",
        id: "lastName",
        accessor: (d) => d.lastName,
      },
    ],
  },
  {
    Header: "Info",
    columns: [
      {
        Header: "Age",
        accessor: "age",
      },
      {
        Header: "Status",
        accessor: "status",
      },
    ],
  },
  {
    Header: "Stats",
    columns: [
      {
        Header: "Visits",
        accessor: "visits",
      },
    ],
  },
];

class MisTarjetasTest extends React.Component {
  constructor() {
    super();
    this.state = {
      data: makeData(),
      expanded: {},
    };
  }
  render() {
    const { data } = this.state;
    return (
      <div>
        <ReactTable
          data={data}
          columns={columns}
          defaultPageSize={10}
          expanded={this.state.expanded}
          className="-striped -highlight"
          getTrProps={(state, rowInfo, column, instance, expanded) => {
            return {
              onClick: (e) => {
                console.log(rowInfo);
                console.log(rowInfo.viewIndex);
                var rowIndex = "{'" + rowInfo.viewIndex + "':'true'}";
                console.log(rowIndex);
                this.setState({ expanded: {} });
                //this.setState({ expanded: rowIndex });
                //this.setState({ expanded: JSON.parse(rowIndex) });
                this.setState({
                  expanded: "{'" + rowInfo.viewIndex + "':'true'}",
                });
              },
            };
          }}
          SubComponent={(row) => {
            return (
              <div style={{ padding: "20px" }}>
                <em>
                  You can put any component you want here, even another React
                  Table!
                </em>
                <br />
                <br />
                <ReactTable
                  data={data}
                  columns={columns}
                  defaultPageSize={3}
                  showPagination={false}
                  SubComponent={(row) => {
                    return (
                      <div style={{ padding: "20px" }}>
                        Another Sub Component!
                      </div>
                    );
                  }}
                />
              </div>
            );
          }}
        />
        <br />
        <Tips />
        <Logo />
      </div>
    );
  }
}

export default MisTarjetasTest;
