import React, { Component } from "react";
import { Card, CardBody, Row, Col } from "reactstrap";
import moment from "moment";
export class AbiertaDetalle extends Component {
  render() {
    const { tarjetas, link_id } = this.props;
    return (
      <div>
        {tarjetas &&
          tarjetas
            .filter(({ _id }) => _id === link_id)
            .map(
              ({
                estado,
                color,
                detecto,
                prioridad,
                familia,
                fecha,
                maquina,
                equipo,
                sugerencia,
              }) => {
                return (
                  <Card>
                    <CardBody>
                      <Row className="my-2">
                        <Col sm={6}>
                          <h5 className="font-16 font-medium">Estado</h5>
                          <h5 className="font-14 font-weight-normal">
                            {estado}
                          </h5>
                        </Col>
                        <Col sm={6}>
                          <h5 className="font-16 font-medium">Color</h5>
                          <h5 className="font-14 font-weight-normal">
                            {color}
                          </h5>
                        </Col>
                      </Row>
                      <Row className="my-2">
                        <Col sm={6}>
                          <h5 className="font-16 font-medium">Detecto</h5>
                          <h5 className="font-14 font-weight-normal">
                            {detecto}
                          </h5>
                        </Col>
                        <Col sm={6}>
                          <h5 className="font-16 font-medium">Prioridad</h5>
                          <h5 className="font-14 font-weight-normal">
                            {prioridad}
                          </h5>
                        </Col>
                      </Row>
                      <Row className="my-2">
                        {color !== "Amarilla" && (
                          <Col sm={6}>
                            <h5 className="font-16 font-medium">Familia</h5>
                            <h5 className="font-14 font-weight-normal">
                              {familia}
                            </h5>
                          </Col>
                        )}
                        {color === "Amarilla" && (
                          <Col sm={6}>
                            <h5 className="font-16 font-medium">
                              Sugerencia para que no se repita
                            </h5>
                            <h5 className="font-14 font-weight-normal">
                              {sugerencia}
                            </h5>
                          </Col>
                        )}
                        <Col sm={6}>
                          <h5 className="font-16 font-medium">
                            Fecha apertura
                          </h5>
                          <h5 className="font-14 font-weight-normal">
                            {moment(fecha).format("l LTS")}
                          </h5>
                        </Col>
                      </Row>
                      <Row className="my-2">
                        <Col sm={6}>
                          <h5 className="font-16 font-medium">Maquina</h5>
                          <h5 className="font-14 font-weight-normal">
                            {maquina}
                          </h5>
                        </Col>
                        <Col sm={6}>
                          <h5 className="font-16 font-medium">Equipo</h5>
                          <h5 className="font-14 font-weight-normal">
                            {equipo}
                          </h5>
                        </Col>
                      </Row>
                      {color === "Amarilla" && (
                        <Row className="my-2">
                          <Col sm={6}>
                            <h5 className="font-16 font-medium">Maquina</h5>
                            <h5 className="font-14 font-weight-normal">
                              {maquina}
                            </h5>
                          </Col>
                          <Col sm={6}>
                            <h5 className="font-16 font-medium">Equipo</h5>
                            <h5 className="font-14 font-weight-normal">
                              {equipo}
                            </h5>
                          </Col>
                        </Row>
                      )}
                    </CardBody>
                  </Card>
                );
              }
            )}
      </div>
    );
  }
}

export default AbiertaDetalle;
