import React, { Component } from "react";
import { connect } from "react-redux";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import { Card, CardTitle, CardBody, Container, Row, Col } from "reactstrap";
import CanvasJSReact from "./canvasjs.react";

var CanvasJSChart = CanvasJSReact.CanvasJSChart;
var CanvasJS = CanvasJSReact.CanvasJS;

class DashBoard extends Component {
  componentDidMount() {
    this.props.getTarjetas();
  }

  constructor() {
    super();
    this.toggleDataSeries = this.toggleDataSeries.bind(this);
  }
  toggleDataSeries(e) {
    if (typeof e.dataSeries.visible === "undefined" || e.dataSeries.visible) {
      e.dataSeries.visible = false;
    } else {
      e.dataSeries.visible = true;
    }

    this.chart.render();
  }
  render() {
    const { tarjetas } = this.props.tarjetas;

    const abiertasJulio = tarjetas.filter(
      ({ estado, fecha }) =>
        estado === "Abierto" &&
        fecha.substr(4, 4).replace("-", "").replace("-", "") === "06"
    );

    const totalFechasTarjetas = tarjetas.map(({ fecha }) => fecha.substr(0, 7));
    let totalFechasTarjetasUnicos = new Set(totalFechasTarjetas);
    const todasFechas = [...totalFechasTarjetasUnicos];

    const TodasFechasTotal = [
      todasFechas.map((item, index) => {
        return {
          x: new Date(parseInt(item.slice(0, 4)), parseInt(item.slice(5, 7))),
          y: tarjetas
            .filter(
              ({ estado, fecha }) =>
                estado === "Abierto" &&
                fecha.substr(4, 4).replace("-", "").replace("-", "") ===
                  item.slice(5, 7)
            )
            .length.array.slice(0, index + 1)
            .reduce((a, b) => a + b),
        };
      }),
    ];

    console.log(TodasFechasTotal[0]);

    const options = {
      animationEnabled: true,

      axisX: {
        valueFormatString: "MMMM",
      },
      axisY: {
        title: "Cantidad de tarjetas",
        lineColor: "#4F81BC",
        tickColor: "#4F81BC",
        labelFontColor: "#4F81BC",
      },
      axisY2: {
        title: "% de Cierre",
        suffix: "%",

        lineColor: "#C0504E",
        tickColor: "#C0504E",
        labelFontColor: "#C0504E",
      },
      toolTip: {
        shared: true,
      },
      legend: {
        cursor: "pointer",
        itemclick: this.toggleDataSeries,
        verticalAlign: "top",
      },
      data: [
        {
          color: "#DBE034",
          type: "column",
          name: "Amarillas acumuladas (abiertas)",
          showInLegend: true,
          xValueFormatString: "MMMM YYYY",

          dataPoints: [
            { x: new Date(2020, 0), y: 3 },
            { x: new Date(2020, 1), y: 2 },
            { x: new Date(2020, 2), y: 4 },
            { x: new Date(2020, 3), y: 3 },
            { x: new Date(2020, 4), y: 4 },
            { x: new Date(2020, 5), y: 5 },
            { x: new Date(2020, 6), y: abiertasJulio.length },
            { x: new Date(2020, 7), y: 4 },
            { x: new Date(2020, 8), y: 6 },
            { x: new Date(2020, 9), y: 3 },
            { x: new Date(2020, 10), y: 4 },
            { x: new Date(2020, 11), y: 5 },
          ],
        },
        {
          type: "line",

          name: "Porcentaje Amarillas Cerradas",
          showInLegend: true,
          yValueFormatString: "$#,##0",
          dataPoints: [
            { x: new Date(2020, 0), y: 7 },
            { x: new Date(2020, 1), y: 5 },
            { x: new Date(2020, 2), y: 6 },
            { x: new Date(2020, 3), y: 7 },
            { x: new Date(2020, 4), y: 8 },
            { x: new Date(2020, 5), y: 5 },
            { x: new Date(2020, 6), y: 3 },
            { x: new Date(2020, 7), y: 5 },
            { x: new Date(2020, 8), y: 2 },
            { x: new Date(2020, 9), y: 2 },
            { x: new Date(2020, 10), y: 4 },
            { x: new Date(2020, 11), y: 3 },
          ],
        },
        {
          type: "line",

          name: "Porcentaje Amarillas Cerradas",
          showInLegend: true,
          axisYType: "secondary",
          yValueFormatString: "$#,##0",
          dataPoints: [
            { x: new Date(2020, 0), y: 10 },
            { x: new Date(2020, 1), y: 2 },
            { x: new Date(2020, 2), y: 66 },
            { x: new Date(2020, 3), y: 3 },
            { x: new Date(2020, 4), y: 4 },
            { x: new Date(2020, 5), y: 5 },
            { x: new Date(2020, 6), y: abiertasJulio.length },
            { x: new Date(2020, 7), y: 4 },
            { x: new Date(2020, 8), y: 6 },
            { x: new Date(2020, 9), y: 3 },
            { x: new Date(2020, 10), y: 4 },
            { x: new Date(2020, 11), y: 5 },
          ],
        },
      ],
    };
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <div>
                <h2 className="mb-3">Evolucion de Tarjetas amarillas</h2>
              </div>

              <Row>
                <Col sm={12} lg={6} md={6}>
                  <CanvasJSChart
                    options={options}
                    onRef={(ref) => (this.chart = ref)}
                  />
                </Col>
                <Col>
                  <Card></Card>
                </Col>
              </Row>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
  };
};
export default connect(mapStateToProps, { getTarjetas })(DashBoard);
