import {
  GET_KAIZEN,
  CARGANDO_KAIZEN,
  AGREGAR_KAIZEN,
  CERRAR_KAIZEN,
  BORRAR_TARJETA,
} from "../actions/types";
const initState = {
  tarjetaskaizen: [],
  cargando: false,
  agregarsuccess: false,
  tarjetaActualId: "",
};

export default function (state = initState, action) {
  switch (action.type) {
    case GET_KAIZEN:
      return {
        ...state,
        tarjetaskaizen: action.payload,
        cargando: false,
        agregarsuccess: false,
        tarjetaActualId: "",
      };
    case AGREGAR_KAIZEN:
      return {
        ...state,
        tarjetaskaizen: [action.payload, ...state.tarjetaskaizen],
        agregarsuccess: true,
        tarjetaActualId: action.payload._id,
      };
    case CERRAR_KAIZEN:
      return Object.assign({}, state, {
        tarjetaskaizen: state.tarjetaskaizen.map((kaizen) => {
          return kaizen._id === action.payload._id ? action.payload : kaizen;
        }), // replace matched item and returns the array
        agregarsuccess: true,
      });
    case CARGANDO_KAIZEN:
      return {
        ...state,
        cargando: true,
      };
    case BORRAR_TARJETA:
      return {
        ...state,
        tarjetaskaizen: state.tarjetaskaizen.filter(
          (kaizen) => kaizen._id !== action.payload
        ),
      };
    default:
      return state;
  }
}
