import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import ReactHTMLTableToExcel from "react-html-table-to-excel";
import {
  Card,
  CardBody,
  Row,
  Col,
  Table,
  Button,
  Container,
  Input,
  Label,
} from "reactstrap";
import Select from "react-select";
import { array } from "prop-types";
const options = [
  { value: "N°", label: "numero" },
  { value: "Color", label: "color" },
  { value: "Prioridad", label: "prioridad" },
  { value: "Equipo Autonomo", label: "equipo" },
  { value: "Fecha apertura", label: "fecha" },
  { value: "Descripcion anomalia", label: "descripcion" },
  { value: "Estado acutal", label: "estado" },
];
class MisTarjetasFiltro extends Component {
  componentDidMount() {
    this.props.getTarjetas();
  }
  state = {
    selectedOption: null,
    numero: "",
    color: "",
    equipo: "",
    prioridad: "",
    fecha: "",
    descripcion: "",
    estado: "",
  };
  handleChange = (selectedOption) => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption);
  };
  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  render() {
    const { tarjetas } = this.props.tarjetas;
    const { selectedOption } = this.state;
    var filter = {
      numero: this.state.numero && this.state.numero,
      color: this.state.color && this.state.color,
      equipo: this.state.equipo && this.state.equipo,
      prioridad: this.state.prioridad && this.state.prioridad,
      fecha: this.state.fecha && this.state.fecha,
      descripcion: this.state.descripcion && this.state.descripcion,
      estado: this.state.estado && this.state.estado,
    };

    const multiFilter = (arr, filters) => {
      const filterKeys = Object.keys(filters);
      return arr.filter((eachObj) => {
        return filterKeys.every((eachKey) => {
          if (!filters[eachKey].length) {
            return true; // passing an empty filter means that filter is ignored.
          }
          return filters[eachKey].includes(eachObj[eachKey]);
        });
      });
    };
    const newFilter = multiFilter(tarjetas, filter);

    const globalArray = {
      color: ["Azul", "Roja", "Amarilla", "Verde"],
    };

    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <Row>
                <Col>
                  <div className="d-flex align-items-center">
                    <div className="">
                      <h2 className="mb-3">Tabla dinamica de tarjetas</h2>
                    </div>
                    <div className="ml-auto d-flex no-block align-items-center">
                      <div className="dl">
                        <ReactHTMLTableToExcel
                          className="btn btn-info"
                          table="emp"
                          filename="ReporteTarjetas"
                          sheet="Tarjetas"
                          buttonText="Exportar excel"
                        />
                      </div>
                    </div>
                  </div>
                </Col>
              </Row>
              <div className="mb-3">
                <h5>Seleccionar elementos para la tabla</h5>
                <Select
                  value={selectedOption}
                  onChange={this.handleChange}
                  options={options}
                  isMulti
                  placeholder="Seleccionar"
                />
              </div>
              <h5>Filtrar elementos</h5>
              <Row className="mb-3">
                {selectedOption &&
                  selectedOption.map(({ label, value }) => {
                    return (
                      <Col>
                        <div>
                          <Label for="color">{value}</Label>
                          <Input
                            type="select"
                            name={label}
                            id={label}
                            onChange={this.onChange}
                          >
                            <option></option>
                            {globalArray[label].map((item) => (
                              <option>{item}</option>
                            ))}
                          </Input>
                        </div>
                      </Col>
                    );
                  })}
              </Row>
              <Card>
                <CardBody>
                  <Table className="no-wrap v-middle" responsive id="emp">
                    <thead>
                      <tr className="border-0">
                        {selectedOption &&
                          selectedOption.map(({ value }) => {
                            return <th className="border-0">{value}</th>;
                          })}
                      </tr>
                    </thead>

                    {newFilter &&
                      newFilter.map((item, index) => {
                        return (
                          <tbody key={index}>
                            <tr>
                              {selectedOption &&
                                selectedOption.map(
                                  ({ value, label }, index) => {
                                    return <td key={index}>{item[label]}</td>;
                                  }
                                )}
                            </tr>
                          </tbody>
                        );
                      })}
                  </Table>
                </CardBody>
              </Card>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
    user: state.auth.user,
  };
};

export default connect(mapStateToProps, { getTarjetas })(MisTarjetasFiltro);
