import { GET_TARJETAS, CARGANDO_TARJETAS, AGREGAR_TARJETA } from "./types";

import { returnErrors } from "./errorActions";
import axios from "axios";

export const getTarjetas = () => (dispatch) => {
  dispatch(cargandoTarjetas());
  axios
    .get("/api/tarjetas")
    .then((res) =>
      dispatch({
        type: GET_TARJETAS,
        payload: res.data,
      })
    )
    .catch((err) =>
      dispatch(returnErrors(err.response.data, err.response.status))
    );
};

export const agregarTarjeta = (tarjeta) => (dispatch, getState) => {
  axios
    .post("/api/tarjetas", tarjeta)
    .then((res) =>
      dispatch({
        type: AGREGAR_TARJETA,
        payload: res.data,
      })
    )
    .catch((err) =>
      dispatch(
        returnErrors(
          err.response.data,
          err.response.status,
          "AGREGAR_TARJETA_ERROR"
        )
      )
    );
};

export const cargandoTarjetas = () => {
  return {
    type: CARGANDO_TARJETAS,
  };
};
