import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import {
  Card,
  CardBody,
  Row,
  Col,
  Table,
  Button,
  Container,
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  Label,
  Input,
  DropdownItem,
} from "reactstrap";
import moment from "moment";

class MisTarjetasFiltro extends Component {
  componentDidMount() {
    this.props.getTarjetas();
  }
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { tarjetas } = this.props.tarjetas;

    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <Row>
                <Col>
                  <div>
                    <h2 className="mb-3">Tarjetas</h2>
                  </div>

                  <div className="ml-auto d-flex no-block align-items-center mb-3">
                    <div className="dl">
                      <h5 className="font-weight-normal">Filtrar tarjetas</h5>
                    </div>
                  </div>
                </Col>
              </Row>
              <div className="mb-3">
                <ButtonDropdown isOpen={dropdownOpen} toggle={toggle}>
                  <DropdownToggle caret>Button Dropdown</DropdownToggle>
                  <DropdownMenu>
                    <DropdownItem header>
                      <Label check>
                        <Input
                          type="checkbox"
                          id="sustoObservado"
                          name="sustoObservado"
                          onChange={(e) => {
                            this.onChange({
                              target: {
                                name: e.target.name,
                                value: e.target.checked,
                              },
                            });
                          }}
                        />
                        Reporte de Incidente "Susto" observado
                      </Label>
                    </DropdownItem>
                    <DropdownItem disabled>Action</DropdownItem>
                    <DropdownItem>Another Action</DropdownItem>
                    <DropdownItem divider />
                    <DropdownItem>Another Action</DropdownItem>
                  </DropdownMenu>
                </ButtonDropdown>
              </div>
              <Card>
                <CardBody>
                  <Table className="no-wrap v-middle" responsive>
                    <thead>
                      <tr className="border-0">
                        <th className="border-0">N°</th>
                        <th className="border-0">Color</th>
                        <th className="border-0">Prioridad</th>
                        <th className="border-0">Familia</th>
                        <th className="border-0">Fecha apertura</th>
                        <th className="border-0">Descripcion anomalia</th>
                        <th className="border-0">Estado actual</th>
                      </tr>
                    </thead>
                    {tarjetas &&
                      tarjetas.map(
                        ({
                          numero,
                          color,
                          prioridad,
                          familia,
                          fecha,
                          descripcion,
                          estado,
                          _id,
                        }) => {
                          return (
                            <tbody key={_id}>
                              <tr>
                                <td>
                                  <div className="d-flex no-block align-items-center">
                                    <div className="">
                                      <Link
                                        to={{ pathname: `/tarjeta/${_id}` }}
                                      >
                                        <h5 className="mb-0 font-16 font-medium">
                                          {numero}
                                        </h5>
                                      </Link>
                                    </div>
                                  </div>
                                </td>
                                {color === "Azul" ? (
                                  <td>
                                    <li className="border-0 p-0 text-info list-inline-item">
                                      <i className="fa fa-circle"></i> {color}
                                    </li>
                                  </td>
                                ) : null}
                                {color === "Roja" ? (
                                  <td>
                                    <li className="border-0 p-0 text-danger list-inline-item">
                                      <i className="fa fa-circle"></i> {color}
                                    </li>
                                  </td>
                                ) : null}
                                {color === "Verde" ? (
                                  <td>
                                    <li className="border-0 p-0 text-success list-inline-item">
                                      <i className="fa fa-circle"></i> {color}
                                    </li>
                                  </td>
                                ) : null}
                                {color === "Amarilla" ? (
                                  <td>
                                    <li className="border-0 p-0 text-warning list-inline-item">
                                      <i className="fa fa-circle"></i> {color}
                                    </li>
                                  </td>
                                ) : null}

                                <td>{prioridad}</td>

                                <td>{familia}</td>
                                <td>{moment(fecha).format("l LTS")}</td>
                                <td>{descripcion}</td>

                                <td>{estado}</td>
                                <td>
                                  <Link to={{ pathname: `/tarjeta/${_id}` }}>
                                    <Button
                                      color="success"
                                      className="btn bg-secondary border border-secondary"
                                    >
                                      Ver detalle
                                    </Button>
                                  </Link>
                                </td>
                              </tr>
                            </tbody>
                          );
                        }
                      )}
                  </Table>
                </CardBody>
              </Card>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
    user: state.auth.user,
  };
};
export default connect(mapStateToProps, { getTarjetas })(MisTarjetasFiltro);
