import React, { useState } from "react";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import { Image } from "cloudinary-react";
import { connect } from "react-redux";
import { agregarImagen } from "../../../store/actions/tarjetaActions";
import Axios from "axios";

const UploadImageModal = (props) => {
  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  const [imageSelected, setImageSelected] = useState("");
  const [status, setStatus] = useState("");

  const uploadImage = () => {
    const formData = new FormData();
    formData.append("file", imageSelected);
    formData.append("upload_preset", "l0pg2e8q");

    Axios.post(
      "https://api.cloudinary.com/v1_1/dsdwkrbkr/image/upload",
      formData
    ).then((response) => {
      setStatus(response.status);

      props.agregarImagen(response.data.secure_url);
    });
  };
  if (status === 200) {
    return toggle;
  }
  return (
    <div>
      <Button color="secondary" onClick={toggle}>
        Adjuntar Imagen
      </Button>
      <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle}>Modal title</ModalHeader>
        <div>
          <input
            type="file"
            onChange={(event) => {
              setImageSelected(event.target.files[0]);
            }}
          />
        </div>
        <Button color="secondary" onClick={uploadImage}>
          Subir
        </Button>
      </Modal>
    </div>
  );
};

export default connect(null, { agregarImagen })(UploadImageModal);
