import React, { useState } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Input,
  Table,
  FormGroup,
  Col,
  Row,
  Form,
} from "reactstrap";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { getTarjetas } from "../../../store/actions/tarjetaActions";
import { colorynumero } from "../../../store/actions/kaizenActions";

const CynTarjetasModal = (props) => {
  const { buttonLabel, className } = props;

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  const { tarjetas } = props.tarjetas;

  const [numero, setNumero] = useState("");
  const [color, setColor] = useState("");

  const handleNumero = (e) => {
    setNumero(e.target.value);
  };

  const handleColor = (e) => {
    setColor(e.target.value);
  };

  const submitForm = (e) => {
    e.preventDefault();

    const colorynumero = {
      _id: props._id,
      numero,
      color,
    };

    console.log(colorynumero);

    props.colorynumero(colorynumero);
  };

  return (
    <div>
      <p onClick={toggle}>Ver</p>
      <Modal isOpen={modal} toggle={toggle} className={className}>
        <ModalHeader toggle={toggle}>Color y Numero Tarjetas</ModalHeader>
        <ModalBody>
          <Table>
            <thead>
              <tr>
                <th>Numero</th>
                <th>Color</th>
                <th>Estado</th>
                <th></th>
              </tr>
            </thead>

            {props.preTarjeta &&
              props.preTarjeta.map((item, index) => {
                const idTarjeta =
                  tarjetas &&
                  tarjetas
                    .filter(
                      ({ numero, color }) =>
                        color === item.color && numero === item.numero
                    )
                    .map(({ _id }) => {
                      return _id;
                    });
                const link = `/tarjeta/${idTarjeta}`;
                return (
                  <tr>
                    <th scope="row">{item.numero}</th>

                    <td>{item.color}</td>
                    <td>
                      {tarjetas &&
                        tarjetas
                          .filter(
                            ({ numero, color }) =>
                              color === item.color && numero === item.numero
                          )
                          .map(({ estado }) => {
                            return estado;
                          })}
                    </td>
                  </tr>
                );
              })}

            <tr>
              <td>
                <Input
                  className="mt-3"
                  type="number"
                  name="numero"
                  id="numero"
                  onChange={handleNumero}
                  placeholder="Numero"
                ></Input>
              </td>
              <td>
                <Input
                  className="mt-3"
                  type="text"
                  name="color"
                  id="color"
                  onChange={handleColor}
                  placeholder="Color"
                ></Input>
              </td>
            </tr>
            <form onSubmit={submitForm}>
              <Button className="mt-3">Subir</Button>
            </form>
          </Table>
        </ModalBody>
      </Modal>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
  };
};
export default connect(mapStateToProps, { getTarjetas, colorynumero })(
  CynTarjetasModal
);
