import React, { Component } from "react";
import CerrarTarjetaModal from "../cerrartarjeta/CerrarTarjetaModal";
import CerrarTarjetaAmarillaModal from "../cerrartarjeta/CerrarTarjetaAmarillaModal";
import QRModal from "./QRModal";
import { withRouter, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { borrarTarjeta } from "../../../store/actions/tarjetaActions";
import PlanificacionModal from "../planificaciondetalle/PlanificacionModal";
import {
  Button,
  Row,
  Col,
  UncontrolledButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from "reactstrap";
import EditarTarjetaModal from "../editartarjeta/EditarTarjetaModal";
import EditarTarjetaModalAmarilla from "../editartarjeta/EditarTarjetaModalAmarilla";
export class TextDetail extends Component {
  constructor(props) {
    super(props);
    this.wrapper = React.createRef();
  }
  onDeleteClick = (id) => {
    this.props.borrarTarjeta(id);
    this.props.history.push("/tarjetas");
  };

  state = {
    dropDown: false,
    qrmodal: false,
  };
  toogle = () => {
    this.setState({ dropDown: !this.state.dropDown });
  };
  setqrmodal = () => {
    this.setState({ qrmodal: !this.state.qrmodal });
  };

  render() {
    const { tarjetas, link_id } = this.props;
    const completeLink = window.location.href.replace(
      `/tarjeta/${link_id}`,
      ""
    );

    return (
      <div>
        {tarjetas &&
          tarjetas
            .filter(({ _id }) => _id === link_id)
            .map(
              ({
                detecto,
                numero,
                descripcion,
                prioridad,
                familia,
                fecha,
                maquina,
                equipo,
                sugerencia,
                tipodeRiesgo,
                riesgoInicial,
                sustoExperimentado,
                sustoObservado,
                impactoAmbiente,
                color,
                estado,
                _id,
                inicioReparacion,
                finReparacion,
                responsable,
                tiempoEmpleado,
                causa,
                tareaRealizada,
                accionesComplementarias,
                riesgoFinal,
                tipoAccion,
                materialUtilizado,
              }) => {
                return (
                  <div className="mb-3 break-text">
                    <Row>
                      <Col>
                        <div className="d-flex align-items-center">
                          <div>
                            <h2 className="mb-3">
                              Tarjeta {color} NÂ° {numero}
                            </h2>
                          </div>

                          <div className="ml-auto d-flex no-block align-items-center">
                            <div className="dl">
                              <Row>
                                <UncontrolledButtonDropdown>
                                  <DropdownToggle caret>
                                    Opciones
                                  </DropdownToggle>
                                  <DropdownMenu>
                                    <DropdownItem
                                      onClick={() =>
                                        this.setState({
                                          qrmodal: true,
                                        })
                                      }
                                    >
                                      Action 1
                                    </DropdownItem>

                                    {this.state.qrmodal && (
                                      <QRModal
                                        path={this.props.location.pathname}
                                        color={color}
                                        isOpen={this.state.qrmodal}
                                        numero={numero}
                                        completePath={completeLink}
                                      >
                                        Mostrar QR
                                      </QRModal>
                                    )}

                                    <DropdownItem divider />

                                    {estado === "Abierta" &&
                                      color === "Amarilla" && (
                                        <CerrarTarjetaAmarillaModal
                                          _id={link_id}
                                          color={color}
                                        ></CerrarTarjetaAmarillaModal>
                                      )}
                                    {estado === "Abierta" &&
                                      color !== "Amarilla" && (
                                        <CerrarTarjetaModal
                                          _id={link_id}
                                          color={color}
                                        ></CerrarTarjetaModal>
                                      )}

                                    {localStorage.token &&
                                      this.props.user &&
                                      (this.props.user.role ===
                                        "Jefe de area" ||
                                        "Admin") &&
                                      color === "Amarilla" && (
                                        <div>
                                          <DropdownItem divider />
                                          <EditarTarjetaModalAmarilla
                                            tarjeta={{
                                              _id,
                                              descripcion,
                                              detecto,
                                              prioridad,
                                              maquina,
                                              familia,
                                              equipo,
                                              sustoExperimentado,
                                              sustoObservado,
                                              impactoAmbiente,
                                              sugerencia,
                                              tipodeRiesgo,
                                              riesgoInicial,
                                              responsable,
                                              estado,
                                              tareaRealizada,
                                              riesgoFinal,
                                              tipoAccion,
                                              accionesComplementarias,
                                            }}
                                          ></EditarTarjetaModalAmarilla>
                                        </div>
                                      )}
                                    {localStorage.token &&
                                      this.props.user &&
                                      (this.props.user.role ===
                                        "Jefe de area" ||
                                        "Admin") &&
                                      color !== "Amarilla" && (
                                        <EditarTarjetaModal
                                          tarjeta={{
                                            numero,
                                            descripcion,
                                            prioridad,
                                            familia,
                                            fecha,
                                            maquina,
                                            estado,
                                            equipo,
                                            sugerencia,
                                            detecto,
                                            tipodeRiesgo,
                                            riesgoInicial,
                                            color,
                                            inicioReparacion,
                                            finReparacion,
                                            responsable,
                                            riesgoFinal,
                                            tiempoEmpleado,
                                            causa,
                                            tareaRealizada,
                                            tipoAccion,
                                            materialUtilizado,
                                            _id,
                                          }}
                                        ></EditarTarjetaModal>
                                      )}

                                    {localStorage.token &&
                                      this.props.user &&
                                      this.props.user.role === "Admin" && (
                                        <div>
                                          <DropdownItem divider />
                                          <p
                                            className="my-3 mx-3 text-dark"
                                            style={{ cursor: "pointer" }}
                                            onClick={this.onDeleteClick.bind(
                                              this,
                                              _id
                                            )}
                                          >
                                            Borrar Tarjeta
                                          </p>
                                        </div>
                                      )}

                                    <DropdownItem divider />
                                    <PlanificacionModal></PlanificacionModal>
                                    {/* <DropdownItem divider /> */}
                                    {/* <p
                                      className="my-3 mx-3 text-dark"
                                      style={{ cursor: "pointer" }}
                                    >
                                      Adjuntar Imagen
                                    </p> */}
                                  </DropdownMenu>
                                </UncontrolledButtonDropdown>
                              </Row>
                            </div>
                          </div>
                        </div>
                      </Col>
                    </Row>
                  </div>
                );
              }
            )}
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    users: state.users,
    user: state.auth.user,
    isAuthenticated: state.auth.isAuthenticated,
  };
};
export default connect(mapStateToProps, { borrarTarjeta })(
  withRouter(TextDetail)
);
