import React, { Component } from "react";

import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  Label,
  Input,
  FormGroup,
  NavLink,
  Alert,
} from "reactstrap";

import { connect } from "react-redux";
import PropTypes from "prop-types";
import { login } from "../../actions/authActions";
import { agregarFilter } from "../../store/actions/filterActions";

class PresetModal extends Component {
  state = {
    modal: false,
    email: "",
    password: "",
    msg: null,
  };

  static propTypes = {
    isAuthenticated: PropTypes.bool,
    error: PropTypes.object.isRequired,
    login: PropTypes.func.isRequired,
    clearErrors: PropTypes.func.isRequired,
  };

  componentDidUpdate(prevProps) {
    const { error, isAuthenticated } = this.props;
    if (error !== prevProps.error) {
      //Check for login error
      if (error.id === "LOGIN_FAIL") {
        this.setState({
          msg: error.msg.msg,
        });
      } else {
        this.setState({
          msg: null,
        });
      }
    }
    // If auth, close modal
    if (this.state.modal) {
      if (isAuthenticated) {
        this.toggle();
      }
    }
  }
  toggle = () => {
    // Clear errors
    this.props.clearErrors();
    this.setState({
      modal: !this.state.modal,
    });
  };
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  onSubmit = (e) => {
    const {
      selectedOption,
      numero,
      color,
      equipo,
      prioridad,
      fecha,
      descripcion,
      estado,
      maquina,
      detecto,
      familia,
      qrcode,
    } = this.props.state;

    // Crear Filter
    const nuevoFilter = {
      selectedOption,
      numero,
      color,
      equipo,
      prioridad,
      fecha,
      descripcion,
      estado,
      maquina,
      detecto,
      familia,
      qrcode,
    };
    this.props.agregarFilter(nuevoFilter);
  };
  render() {
    return (
      <div>
        <Button className="ml-3">Save</Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>
            Guardar como preestablecido
          </ModalHeader>
          <ModalBody>
            <Form onSubmit={this.onSubmit}>
              <FormGroup>
                <Label for="nombre">Nombre</Label>
                <Input
                  type="text"
                  name="nombre"
                  id="nombre"
                  placeholder="Nombre"
                  className="mb-3"
                  onChange={this.onChange}
                />

                <Button color="dark" block style={{ marginTop: "2rem" }}>
                  Subir
                </Button>
              </FormGroup>
            </Form>
          </ModalBody>
        </Modal>
      </div>
    );
  }
}

export default connect(null, { agregarFilter })(PresetModal);
