import React, { Component } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  Label,
  Input,
  FormGroup,
  NavLink,
  Alert,
} from "reactstrap";
import { connect } from "react-redux";
import { parteMaquina } from "../../store/actions/camposActions";
import { getCampos } from "../../store/actions/camposActions";

class CamposParteMaquinaModal extends Component {
  state = {
    modal: false,
    maquina: "",
    name: "",
  };

  componentDidMount() {
    this.props.getCampos();
  }

  toggle = () => {
    this.setState({
      modal: !this.state.modal,
    });
  };
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  onSubmit = (e) => {
    e.preventDefault();
    const { maquina, name } = this.state;

    // Crear Campo
    const nuevoCampos = {
      maquina,
      name,
    };
    e.preventDefault();
    console.log(nuevoCampos);
    //this.props.agregarCampos(nuevoCampos);
    if (this.state.value === "") {
      return null;
    } else {
      return this.toggle();
    }
  };

  render() {
    const { campos } = this.props.campos;
    return (
      <div>
        <Button className="ml-3" onClick={this.toggle} href="#">
          Agregar
        </Button>
        <Row>
          <Col>
            <div className="d-sm-flex align-items-center">
              <div className="">
                <div>
                  <FormGroup>
                    <Label for="maquina">Maquina / Instalaci√≥n</Label>
                    <Input
                      type="select"
                      name="maquina"
                      id="maquina"
                      onChange={(e) => {
                        this.setState({
                          idMaquina: e.target.value,
                        });
                      }}
                    >
                      <option>Seleccionar</option>
                      {campos &&
                        campos
                          .filter(({ name }) => {
                            return name === "maquina";
                          })
                          .map(({ name, value, _id }) => {
                            return <option value={_id}>{value}</option>;
                          })}
                    </Input>
                  </FormGroup>
                </div>
              </div>
              <div className="ml-auto d-sm-flex no-block align-items-center mb-3">
                <CampoModal campo="maquina"></CampoModal>
                <Button
                  onClick={this.onDeleteClickCampo.bind(
                    this,
                    this.state.idMaquina
                  )}
                  className="bg-danger border-danger ml-3"
                >
                  Borrar
                </Button>
              </div>
            </div>
          </Col>
        </Row>
        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>
            Agregar campo parte maquina
          </ModalHeader>
          <ModalBody>
            <Label for="name">Nombre del campo</Label>
            <Input
              type="text"
              name="name"
              id="name"
              placeholder="Nombre del campo"
              className="mb-3"
              onChange={this.onChange}
            />
            <Form onSubmit={this.onSubmit}>
              <Button color="dark" block style={{ marginTop: "2rem" }}>
                Subir
              </Button>
            </Form>
          </ModalBody>
        </Modal>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  campos: state.campos,
});
export default connect(mapStateToProps, { parteMaquina })(
  CamposParteMaquinaModal
);
