import React, { Component } from "react";
import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Container,
  Row,
  Col,
  Alert,
} from "reactstrap";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import { agregarTarjeta } from "../../store/actions/tarjetaActions";

class AÃ±adirTarjeta extends Component {
  componentDidMount() {
    this.props.getTarjetas();
  }
  state = {
    numero: "",
    descripcion: "",
    color: "",
    detecto: "",
    prioridad: "",
    familia: "",
    maquina: "",
    equipo: "",
    riesgoInicial: "",
    tipodeRiesgo: "",
    msg: null,
  };

  componentDidUpdate(prevProps) {
    const { error } = this.props;
    if (error !== prevProps.error) {
      // Check login error
      console.log(error.msg.msg);
      if (error.id === "AGREGAR_TARJETA_ERROR") {
        this.setState({
          msg: error.msg.msg,
        });
      } else {
        this.setState({
          msg: null,
        });
      }
    }
  }

  onChange = (e) => {
