import React, { Component } from "react";
import { Container, Input } from "reactstrap";
import { getTarjetas } from "../store/actions/tarjetaActions";
import { connect } from "react-redux";
class TestQr extends Component {
  componentDidMount() {
    this.props.getTarjetas();
  }
  state = {
    code: "",
  };
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  render() {
    const { tarjetas } = this.props.tarjetas;
    const tarjetaFinal = tarjetas.filter(({ color, numero }) => {
      return (
        (this.state.code === "R" && color === "Roja") ||
        (this.state.code === "A" && color === "Azul")
      );
    });

    console.log(tarjetaFinal);
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <h3>Buscar por codigo</h3>

              <Input
                type="text"
                name="code"
                id="code"
                onChange={this.onChange}
              ></Input>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
  };
};
export default connect(mapStateToProps, { getTarjetas })(TestQr);
