import React, { Component } from "react";
import { connect } from "react-redux";
import { Card, CardBody, Row, Col, Table, Button, Container } from "reactstrap";
import { getUsers } from "../../store/actions/usersActions";
export class AdminUsuarios extends Component {
  componentDidMount() {
    this.props.getUsers();
  }
  render() {
    const { users } = this.props.users;
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <Row>
                <Col>
                  <div className="d-sm-flex align-items-center">
                    <div className="">
                      <div>
                        <h2 className="mb-3">Tarjetas</h2>
                      </div>

                      <div className="ml-auto d-sm-flex no-block align-items-center mb-3 d-sm-flex">
                        <div className="dl">
                          <h5 className="font-weight-normal">
                            Todas las tarjetas en la base de datos
                          </h5>
                        </div>
                      </div>
                    </div>
                    <div className="ml-auto d-sm-flex no-block align-items-center mb-3">
                      <Button color="success" className="btn">
                        Agregar Usuario
                      </Button>
                    </div>
                  </div>
                </Col>
              </Row>
              <Card>
                <CardBody>
                  <Table className="no-wrap v-middle" responsive>
                    <thead>
                      <tr className="border-0">
                        <th className="border-0">Email</th>
                        <th className="border-0">Role</th>
                      </tr>
                    </thead>
                    {users &&
                      users.map(({ name, email, role, _id }) => {
                        return (
                          <tbody key={_id}>
                            <tr>
                              <td>{email}</td>
                              <td>{role}</td>
                            </tr>
                          </tbody>
                        );
                      })}
                  </Table>
                </CardBody>
              </Card>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    users: state.users,
    user: state.auth.user,
  };
};
export default connect(mapStateToProps, { getUsers })(AdminUsuarios);
