import React, { Component } from "react";
import { Card, CardTitle, CardBody, Container, Row, Col } from "reactstrap";
import CanvasJSReact from "./canvasjs.react";

var CanvasJSChart = CanvasJSReact.CanvasJSChart;
class DashBoard extends Component {
  constructor() {
    super();
    this.toggleDataSeries = this.toggleDataSeries.bind(this);
    this.addSymbols = this.addSymbols.bind(this);
  }
  addSymbols(e) {
    var suffixes = ["", "K", "M", "B"];
    var order = Math.max(Math.floor(Math.log(e.value) / Math.log(1000)), 0);
    if (order > suffixes.length - 1) order = suffixes.length - 1;
    var suffix = suffixes[order];
    return CanvasJS.formatNumber(e.value / Math.pow(1000, order)) + suffix;
  }
  toggleDataSeries(e) {
    if (typeof e.dataSeries.visible === "undefined" || e.dataSeries.visible) {
      e.dataSeries.visible = false;
    } else {
      e.dataSeries.visible = true;
    }
    this.chart.render();
  }
  render() {
    const options = {
      data: [
        {
          type: "chartContainer",
          dataPoints: [
            { label: "Total Bugs", y: 4, color: "#3483B3" },
            { label: "Open Bugs", y: 7, color: "#88DD45" },
            { label: "Closed Bugs", y: 2, color: "#DD4545" },
          ],
        },
      ],
    };
    const options2 = {
      data: [
        {
          type: "column",
          dataPoints: [
            { label: "Bug / Errors", y: 3, color: "#3483B3" },
            { label: "Task", y: 5, color: "#88DD45" },
          ],
        },
      ],
    };
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <div>
                <h2 className="mb-3">Dashboard</h2>
              </div>

              <Row>
                <Col>
                  <Card>
                    <CardBody>
                      <div className="d-flex align-items-center">
                        <div className="">
                          <CardTitle>Bugs by status</CardTitle>
                        </div>
                      </div>
                      <CanvasJSChart options={options} />
                    </CardBody>
                  </Card>
                </Col>
                <Col>
                  <Card>
                    <CardBody>
                      <div className="d-flex align-items-center">
                        <div className="">
                          <CardTitle>Bugs by type</CardTitle>
                        </div>
                      </div>
                      <CanvasJSChart options={options2} />
                    </CardBody>
                  </Card>
                </Col>
              </Row>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}

export default DashBoard;
